import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import MainLayout from "@/components/layout/main-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import WorkflowProgress from "@/components/ui/workflow-progress";
import LCSummary from "@/components/lc-summary";
import ContractSummary from "@/components/contract-summary";
import { 
  FileText, 
  Ship, 
  University, 
  CheckCircle, 
  Box,
  Leaf,
  Microchip,
  Plus
} from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";

export default function Dashboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [selectedLC, setSelectedLC] = useState<any>(null);
  const [showLCSummary, setShowLCSummary] = useState(false);
  const [selectedContract, setSelectedContract] = useState<any>(null);
  const [showContractSummary, setShowContractSummary] = useState(false);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/dashboard/stats"],
    retry: false,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  const { data: recentContracts, isLoading: contractsLoading } = useQuery({
    queryKey: ["/api/contracts", { limit: 3 }],
    retry: false,
    refetchInterval: 30000,
  });

  const { data: upcomingVessels, isLoading: vesselsLoading } = useQuery({
    queryKey: ["/api/vessels", { status: "in_transit", limit: 3 }],
    retry: false,
    refetchInterval: 30000,
  });

  const { data: contracts } = useQuery({
    queryKey: ["/api/contracts"],
    retry: false,
    refetchInterval: 30000,
  });

  const { data: lettersOfCredit } = useQuery({
    queryKey: ["/api/letters-of-credit"],
    retry: false,
    refetchInterval: 30000,
  });

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <MainLayout title="Dashboard" subtitle="Welcome back, manage your import operations">
          {/* KPI Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 animate-slide-up">
            <Card className="bg-white/80 backdrop-blur-sm border border-secondary-200/50 shadow-medium card-hover">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-semibold text-secondary-600 uppercase tracking-wide">Active Requests</p>
                    <p className="text-3xl font-bold text-secondary-900 mt-2">
                      {(recentContracts as any[])?.length || 0}
                    </p>
                  </div>
                  <div className="w-14 h-14 bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center shadow-colored">
                    <FileText className="text-white" size={22} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/80 backdrop-blur-sm border border-secondary-200/50 shadow-medium card-hover">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-semibold text-secondary-600 uppercase tracking-wide">Active Contracts</p>
                    <p className="text-3xl font-bold text-secondary-900 mt-2">
                      {(contracts as any[])?.filter((c: any) => c.status === 'approved').length || 0}
                    </p>
                  </div>
                  <div className="w-14 h-14 bg-gradient-to-br from-green-500 to-green-600 rounded-2xl flex items-center justify-center shadow-colored">
                    <CheckCircle className="text-white" size={22} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/80 backdrop-blur-sm border border-secondary-200/50 shadow-medium card-hover">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-semibold text-secondary-600 uppercase tracking-wide">Active LCs</p>
                    <p className="text-3xl font-bold text-secondary-900 mt-2">
                      {(lettersOfCredit as any[])?.filter((lc: any) => lc.status === 'issued').length || 0}
                    </p>
                  </div>
                  <div className="w-14 h-14 bg-gradient-to-br from-purple-500 to-purple-600 rounded-2xl flex items-center justify-center shadow-colored">
                    <University className="text-white" size={22} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white/80 backdrop-blur-sm border border-secondary-200/50 shadow-medium card-hover">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-semibold text-secondary-600 uppercase tracking-wide">Ships in Transit</p>
                    <p className="text-3xl font-bold text-secondary-900 mt-2">
                      {(upcomingVessels as any[])?.length || 0}
                    </p>
                  </div>
                  <div className="w-14 h-14 bg-gradient-to-br from-orange-500 to-orange-600 rounded-2xl flex items-center justify-center shadow-colored">
                    <Ship className="text-white" size={22} />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Recent Activity */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <Card className="bg-white/80 backdrop-blur-sm border border-secondary-200/50 shadow-medium card-hover overflow-hidden">
              <CardHeader className="bg-gradient-to-r from-secondary-50 to-white border-b border-secondary-100/50">
                <CardTitle className="text-xl font-bold text-secondary-900 flex items-center space-x-2">
                  <FileText className="w-5 h-5 text-primary-600" />
                  <span>Recent Contracts</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {contractsLoading ? (
                  <div className="space-y-3">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="h-12 bg-secondary-100 rounded animate-pulse" />
                    ))}
                  </div>
                ) : (recentContracts as any[]) && (recentContracts as any[]).length > 0 ? (
                  <div className="space-y-3">
                    {(recentContracts as any[]).slice(0, 3).map((contract: any) => (
                      <div 
                        key={contract.id} 
                        className="flex items-center justify-between p-4 bg-gradient-to-r from-white to-secondary-50/30 rounded-xl cursor-pointer hover:shadow-soft transition-all duration-200 card-hover border border-secondary-100/50"
                        onClick={() => {
                          setSelectedContract(contract);
                          setShowContractSummary(true);
                        }}
                      >
                        <div className="flex-1">
                          <div className="flex items-center justify-between mb-2">
                            <p className="font-bold text-secondary-900 text-base">CON-{contract.id.toString().padStart(3, '0')}</p>
                            <p className="text-sm font-bold text-primary-600 bg-primary-50 px-2 py-1 rounded-lg">
                              {parseFloat(contract.quantity || 0).toLocaleString()} tons
                            </p>
                          </div>
                          <p className="text-sm text-secondary-600 font-medium">{contract.supplierName}</p>
                        </div>
                        <span className="status-badge text-green-700 bg-green-100 border-green-200">{contract.status}</span>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-secondary-500 text-center py-8">No recent contracts</p>
                )}
              </CardContent>
            </Card>

            <Card className="bg-white/80 backdrop-blur-sm border border-secondary-200/50 shadow-medium card-hover overflow-hidden">
              <CardHeader className="bg-gradient-to-r from-secondary-50 to-white border-b border-secondary-100/50">
                <CardTitle className="text-xl font-bold text-secondary-900 flex items-center space-x-2">
                  <University className="w-5 h-5 text-purple-600" />
                  <span>Letters of Credit</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                {(lettersOfCredit as any[]) && (lettersOfCredit as any[]).length > 0 ? (
                  <div className="space-y-3">
                    {(lettersOfCredit as any[]).map((lc: any) => (
                      <div 
                        key={lc.id}
                        className="w-full p-4 bg-gradient-to-r from-purple-50 to-purple-100/50 rounded-xl border border-purple-200/50 cursor-pointer hover:shadow-medium transition-all duration-200 card-hover"
                        onClick={() => {
                          setSelectedLC(lc);
                          setShowLCSummary(true);
                        }}
                      >
                        <div className="flex items-center space-x-3">
                          <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-purple-600 rounded-xl flex items-center justify-center">
                            <University className="text-white" size={18} />
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-1">
                              <span className="font-bold text-purple-900">{lc.lcNumber}</span>
                              <span className="text-sm font-bold text-purple-700 bg-purple-200 px-2 py-1 rounded-lg">
                                {parseFloat(lc.quantity || 0).toLocaleString()} tons
                              </span>
                            </div>
                            <span className="text-sm text-purple-700 font-medium truncate block">
                              {lc.issuingBank}
                            </span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl flex items-center justify-center">
                      <University className="h-8 w-8 text-gray-400" />
                    </div>
                    <p className="text-secondary-600 text-base font-medium mb-4">No Letters of Credit available</p>
                    <Button 
                      className="btn-primary-gradient px-6 py-2 text-sm font-semibold rounded-xl"
                      onClick={() => window.location.href = '/letters-credit'}
                    >
                      <Plus className="mr-2" size={16} />
                      Create LC
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* LC Summary Dialog */}
          <LCSummary
            lc={selectedLC}
            isOpen={showLCSummary}
            onClose={() => {
              setShowLCSummary(false);
              setSelectedLC(null);
            }}
          />

          {/* Contract Summary Dialog */}
          <ContractSummary
            contract={selectedContract}
            isOpen={showContractSummary}
            onClose={() => {
              setShowContractSummary(false);
              setSelectedContract(null);
            }}
          />
    </MainLayout>
  );
}

client/src/pages/documents.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import MainLayout from "@/components/layout/main-layout";
import DocumentVoting from "@/components/document-voting";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Plus, FileText, Upload, Download, Vote, Trash2 } from "lucide-react";

// Document types for different stages of import
const DOCUMENT_TYPES = [
  { value: "invoice", label: "Commercial Invoice" },
  { value: "packing_list", label: "Packing List" },
  { value: "certificate_origin", label: "Certificate of Origin" },
  { value: "bill_of_lading", label: "Bill of Lading" },
  { value: "insurance_certificate", label: "Insurance Certificate" },
  { value: "quality_certificate", label: "Quality Certificate" },
  { value: "phytosanitary", label: "Phytosanitary Certificate" },
  { value: "customs_declaration", label: "Customs Declaration" },
  { value: "inspection_report", label: "Inspection Report" },
  { value: "weight_certificate", label: "Weight Certificate" },
  { value: "fumigation_certificate", label: "Fumigation Certificate" },
  { value: "other", label: "Other Document" },
];

export default function Documents() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const queryClient = useQueryClient();
  const [showUploadDialog, setShowUploadDialog] = useState(false);
  const [showVotingDialog, setShowVotingDialog] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState<any>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [documentType, setDocumentType] = useState("");
  const [documentTitle, setDocumentTitle] = useState("");
  const [relatedEntity, setRelatedEntity] = useState("");
  const [relatedEntityId, setRelatedEntityId] = useState("");

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Fetch requests for linking documents
  const { data: requests = [] } = useQuery({
    queryKey: ["/api/requests"],
    retry: false,
  });

  // Fetch contracts for linking documents
  const { data: contracts = [] } = useQuery({
    queryKey: ["/api/contracts"],
    retry: false,
  });

  // Fetch vessels for linking documents
  const { data: vessels = [] } = useQuery({
    queryKey: ["/api/vessels"],
    retry: false,
  });

  // Fetch all document votes (this will serve as our document list)
  const { data: documents = [], isLoading: documentsLoading } = useQuery({
    queryKey: ["/api/documents"],
    queryFn: async () => {
      // Since we don't have a dedicated documents endpoint, 
      // we'll aggregate document votes from different entities
      const allDocs: any[] = [];

      // Get documents from requests
      for (const request of requests) {
        try {
          const requestDocs = await apiRequest("GET", `/api/document-votes/request/${request.id}`);
          allDocs.push(...(requestDocs as any[]).map(doc => ({ ...doc, sourceType: 'request', sourceTitle: request.title })));
        } catch (error) {
          console.error(`Failed to fetch documents for request ${request.id}`);
        }
      }

      // Get documents from contracts
      for (const contract of contracts) {
        try {
          const contractDocs = await apiRequest("GET", `/api/document-votes/contract/${contract.id}`);
          allDocs.push(...(contractDocs as any[]).map(doc => ({ ...doc, sourceType: 'contract', sourceTitle: contract.supplierName || `Contract ${contract.id}` })));
        } catch (error) {
          console.error(`Failed to fetch documents for contract ${contract.id}`);
        }
      }

      // Get documents from vessels
      for (const vessel of vessels) {
        try {
          const vesselDocs = await apiRequest("GET", `/api/vessels/${vessel.id}/documents`);
          allDocs.push(...(vesselDocs as any[]).map(doc => ({ 
            ...doc, 
            sourceType: 'vessel', 
            sourceTitle: vessel.vesselName,
            entityType: 'vessel',
            entityId: vessel.id,
            fileName: doc.fileName,
            filePath: doc.filePath
          })));
        } catch (error) {
          console.error(`Failed to fetch documents for vessel ${vessel.id}`);
        }
      }

      return allDocs;
    },
    enabled: !!(requests.length || contracts.length || vessels.length),
    retry: false,
  });

  const uploadMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      const response = await fetch('/api/upload-document', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/documents"] });
      setShowUploadDialog(false);
      setSelectedFile(null);
      setDocumentType("");
      setDocumentTitle("");
      setRelatedEntity("");
      setRelatedEntityId("");
      toast({
        title: "Success",
        description: "Document uploaded successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to upload document",
        variant: "destructive",
      });
    },
  });

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
      if (!allowedTypes.includes(file.type)) {
        toast({
          title: "Invalid file type",
          description: "Please upload a PDF, DOC, or DOCX file",
          variant: "destructive",
        });
        return;
      }

      if (file.size > 10 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please upload a file smaller than 10MB",
          variant: "destructive",
        });
        return;
      }

      setSelectedFile(file);
    }
  };

  const handleUpload = () => {
    if (!selectedFile || !documentType || !documentTitle || !relatedEntity) {
      toast({
        title: "Missing information",
        description: "Please fill in all required fields and select a file",
        variant: "destructive",
      });
      return;
    }

    // Additional validation for when a specific entity is selected but no ID is provided
    if (relatedEntity !== 'general' && !relatedEntityId) {
      toast({
        title: "Missing selection",
        description: `Please select a ${relatedEntity}`,
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('entityType', relatedEntity || 'general');
    formData.append('entityId', relatedEntityId || '0');
    formData.append('documentType', documentType);
    formData.append('title', documentTitle);

    uploadMutation.mutate(formData);
  };

  const getStatusBadge = (votes: any[]) => {
    const approvals = votes?.filter(v => v.vote === 'approve').length || 0;
    const rejections = votes?.filter(v => v.vote === 'reject').length || 0;
    const pending = votes?.filter(v => v.vote === 'pending').length || 0;

    if (rejections > 0) {
      return <Badge variant="destructive">Rejected</Badge>;
    }
    if (pending > 0) {
      return <Badge variant="secondary">Pending Review</Badge>;
    }
    if (approvals > 0) {
      return <Badge variant="default" className="bg-green-600">Approved</Badge>;
    }
    return <Badge variant="outline">No votes</Badge>;
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "high": return "bg-red-100 text-red-800";
      case "medium": return "bg-yellow-100 text-yellow-800";
      case "low": return "bg-green-100 text-green-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  // Group documents by entity
  const groupedDocuments = documents.reduce((acc: any, doc: any) => {
    const key = `${doc.entityType}_${doc.entityId}`;
    if (!acc[key]) {
      acc[key] = {
        entityType: doc.entityType,
        entityId: doc.entityId,
        sourceTitle: doc.sourceTitle,
        documents: []
      };
    }
    acc[key].documents.push(doc);
    return acc;
  }, {});

  return (
    <MainLayout title="Documents" subtitle="Manage import documents and approvals">
          <div className="mb-6">
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
              <div>
                <h1 className="text-2xl font-bold text-secondary-900">Document Management</h1>
                <p className="text-secondary-600">Upload and manage import-related documents</p>
              </div>

              <Dialog open={showUploadDialog} onOpenChange={setShowUploadDialog}>
                <DialogTrigger asChild>
                  <Button className="bg-primary-500 hover:bg-primary-600 text-black border-2 border-primary-700 hover:border-primary-800 w-full sm:w-auto">
                    <Plus size={20} className="mr-2" />
                    Upload Document
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-md">
                  <DialogHeader>
                    <DialogTitle>Upload New Document</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="documentTitle">Document Title *</Label>
                      <Input
                        id="documentTitle"
                        value={documentTitle}
                        onChange={(e) => setDocumentTitle(e.target.value)}
                        placeholder="Enter document title"
                      />
                    </div>

                    <div>
                      <Label htmlFor="documentType">Document Type *</Label>
                      <Select value={documentType} onValueChange={setDocumentType}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select document type" />
                        </SelectTrigger>
                        <SelectContent>
                          {DOCUMENT_TYPES.map((type) => (
                            <SelectItem key={type.value} value={type.value}>{type.label}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="relatedEntity">Related To *</Label>
                      <Select value={relatedEntity} onValueChange={(value) => {
                        setRelatedEntity(value);
                        setRelatedEntityId("");
                      }}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select entity type" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="request">Import Request</SelectItem>
                          <SelectItem value="contract">Contract</SelectItem>
                          <SelectItem value="vessel">Vessel</SelectItem>
                          <SelectItem value="general">General Document</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    {relatedEntity === 'request' && (
                      <div>
                        <Label htmlFor="relatedRequest">Select Request</Label>
                        <Select value={relatedEntityId} onValueChange={setRelatedEntityId}>
                          <SelectTrigger>
                            <SelectValue placeholder="Select request" />
                          </SelectTrigger>
                          <SelectContent>
                            {(requests as any[]).map((request) => (
                              <SelectItem key={request.id} value={request.id.toString()}>{request.title}</SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}

                    {relatedEntity === 'contract' && (
                      <div>
                        <Label htmlFor="relatedContract">Select Contract</Label>
                        <Select value={relatedEntityId} onValueChange={setRelatedEntityId}>
                          <SelectTrigger>
                            <SelectValue placeholder="Select contract" />
                          </SelectTrigger>
                          <SelectContent>
                            {(contracts as any[]).map((contract) => (
                              <SelectItem key={contract.id} value={contract.id.toString()}>
                                {contract.supplierName || `Contract ${contract.id}`}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}

                    {relatedEntity === 'vessel' && (
                      <div>
                        <Label htmlFor="relatedVessel">Select Vessel *</Label>
                        <Select value={relatedEntityId} onValueChange={setRelatedEntityId}>
                          <SelectTrigger>
                            <SelectValue placeholder="Select vessel" />
                          </SelectTrigger>
                          <SelectContent>
                            {(vessels as any[]).map((vessel) => (
                              <SelectItem key={vessel.id} value={vessel.id.toString()}>
                                {vessel.vesselName} - {vessel.cargoType}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}

                    <div>
                      <Label htmlFor="fileUpload">Choose File *</Label>
                      <Input
                        id="fileUpload"
                        type="file"
                        accept=".pdf,.doc,.docx"
                        onChange={handleFileChange}
                        className="cursor-pointer"
                      />
                      {selectedFile && (
                        <p className="text-sm text-green-600 mt-1">Selected: {selectedFile.name}</p>
                      )}
                    </div>

                    <div className="flex justify-end space-x-3">
                      <Button 
                        variant="outline" 
                        onClick={() => setShowUploadDialog(false)}
                      >
                        Cancel
                      </Button>
                      <Button 
                        onClick={handleUpload}
                        disabled={uploadMutation.isPending}
                      >
                        {uploadMutation.isPending ? "Uploading..." : "Upload Document"}
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </div>

          {/* Document Groups */}
          <div className="space-y-6">
            {documentsLoading ? (
              <Card>
                <CardContent className="p-6">
                  <div className="text-center">Loading documents...</div>
                </CardContent>
              </Card>
            ) : Object.keys(groupedDocuments).length === 0 ? (
              <Card>
                <CardContent className="p-6 text-center text-secondary-600">
                  <FileText size={48} className="mx-auto mb-4 opacity-50" />
                  <p>No documents uploaded yet</p>
                  <p className="text-sm">Upload your first document to get started</p>
                </CardContent>
              </Card>
            ) : (
              Object.values(groupedDocuments).map((group: any, index) => (
                <Card key={index}>
                  <CardHeader>
                    <CardTitle className="flex items-center justify-between">
                      <span>{group.sourceTitle} ({group.entityType})</span>
                      <Badge variant="outline">{group.documents.length} documents</Badge>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead>Document Name</TableHead>
                          <TableHead>Upload Date</TableHead>
                          <TableHead>Status</TableHead>
                          <TableHead>Actions</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {group.documents.map((doc: any) => (
                          <TableRow key={doc.id}>
                            <TableCell className="flex items-center space-x-2">
                              <FileText size={16} />
                              <span>{doc.fileName}</span>
                            </TableCell>
                            <TableCell>
                              {new Date(doc.createdAt).toLocaleDateString()}
                            </TableCell>
                            <TableCell>
                              {getStatusBadge([doc])}
                            </TableCell>
                            <TableCell>
                              <div className="flex items-center space-x-2">
                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => {
                                    setSelectedDocument(doc);
                                    setShowVotingDialog(true);
                                  }}
                                  title="Vote on Document"
                                >
                                  <Vote size={14} />
                                </Button>
                              </div>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </CardContent>
                </Card>
              ))
            )}
          </div>

          {/* Document Voting Dialog */}
          <Dialog open={showVotingDialog} onOpenChange={setShowVotingDialog}>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Document Voting - {selectedDocument?.fileName}</DialogTitle>
              </DialogHeader>
              {selectedDocument && (
                <DocumentVoting 
                  entityType={selectedDocument.entityType}
                  entityId={selectedDocument.entityId}
                  allowVoting={(user as any)?.role === 'admin' || (user as any)?.role === 'manager'}
                />
              )}
            </DialogContent>
          </Dialog>
    </MainLayout>
  );
}

