client/src/pages/reports.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Link } from "wouter";
import MainLayout from "@/components/layout/main-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  BarChart3, 
  Download, 
  Calendar,
  FileText,
  DollarSign,
  Ship,
  TrendingUp,
  Users,
  Package,
  Clock,
  Filter,
  X,
  ExternalLink,
  Settings
} from "lucide-react";

export default function Reports() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [dateRange, setDateRange] = useState({ from: "", to: "" });
  const [selectedReport, setSelectedReport] = useState("overview");

  // Financial page filters
  const [selectedIssuingBank, setSelectedIssuingBank] = useState<string>("all");
  const [selectedLCStatus, setSelectedLCStatus] = useState<string>("all");

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: dashboardStats, isLoading: statsLoading } = useQuery({
    queryKey: ["/api/dashboard/stats"],
    retry: false,
  });

  const { data: requests, isLoading: requestsLoading } = useQuery({
    queryKey: ["/api/requests"],
    retry: false,
  });

  const { data: contracts, isLoading: contractsLoading } = useQuery({
    queryKey: ["/api/contracts"],
    retry: false,
  });

  const { data: lettersOfCredit, isLoading: lcsLoading } = useQuery({
    queryKey: ["/api/letters-of-credit"],
    retry: false,
  });

  const { data: vessels, isLoading: vesselsLoading } = useQuery({
    queryKey: ["/api/vessels"],
    retry: false,
  });

  const { data: settlements, isLoading: settlementsLoading } = useQuery({
    queryKey: ["/api/final-settlements"],
    retry: false,
  });

  const exportReport = (reportType: string) => {
    toast({
      title: "Export Started",
      description: `Exporting ${reportType} report...`,
    });
    // In a real implementation, this would trigger a download
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending": return "bg-yellow-100 text-yellow-800";
      case "approved": return "bg-green-100 text-green-800";
      case "rejected": return "bg-red-100 text-red-800";
      case "in_progress": return "bg-blue-100 text-blue-800";
      case "completed": return "bg-purple-100 text-purple-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const calculateRequestsByStatus = () => {
    if (!requests) return {};
    return requests.reduce((acc: any, request: any) => {
      acc[request.status] = (acc[request.status] || 0) + 1;
      return acc;
    }, {});
  };

  const calculateTotalLCQuantity = () => {
    if (!lettersOfCredit) return 0;
    return lettersOfCredit.reduce((total: number, lc: any) => {
      return total + (lc.quantity || 0);
    }, 0);
  };

  // Get unique issuing banks for filter dropdown
  const getUniqueIssuingBanks = () => {
    if (!lettersOfCredit) return [];
    const banks = [...new Set(lettersOfCredit.map((lc: any) => lc.issuingBank).filter(Boolean))];
    return banks.sort();
  };

  // Get unique LC statuses for filter dropdown
  const getUniqueLCStatuses = () => {
    if (!lettersOfCredit) return [];
    const statuses = [...new Set(lettersOfCredit.map((lc: any) => lc.status).filter(Boolean))];
    return statuses.sort();
  };

  // Filter LCs based on selected filters (using enhanced data)
  const getFilteredLCs = () => {
    const enhancedLCs = getEnhancedLCs();

    return enhancedLCs.filter((lc: any) => {
      const bankMatch = selectedIssuingBank === "all" || lc.issuingBank === selectedIssuingBank;
      const statusMatch = selectedLCStatus === "all" || lc.status === selectedLCStatus;
      return bankMatch && statusMatch;
    });
  };



  // Calculate allocated quantity for an LC (sum of vessel quantities linked to this LC)
  const calculateAllocatedQuantity = (lcId: number) => {
    if (!vessels) return 0;
    return vessels
      .filter((vessel: any) => vessel.lcId === lcId)
      .reduce((total: number, vessel: any) => total + (vessel.quantity || 0), 0);
  };

  // Calculate remaining quantity for an LC
  const calculateRemainingQuantity = (lc: any) => {
    const totalQuantity = lc.quantity || 0;
    const allocatedQuantity = calculateAllocatedQuantity(lc.id);
    return totalQuantity - allocatedQuantity;
  };

  // Get enhanced LC data with quantity breakdowns
  const getEnhancedLCs = () => {
    if (!lettersOfCredit) return [];

    return lettersOfCredit.map((lc: any) => ({
      ...lc,
      totalQuantity: lc.quantity || 0,
      allocatedQuantity: calculateAllocatedQuantity(lc.id),
      remainingQuantity: calculateRemainingQuantity(lc),
    }));
  };

  const calculateAverageProcessingTime = () => {
    if (!requests) return 0;
    const completedRequests = requests.filter((r: any) => r.status === 'completed');
    if (completedRequests.length === 0) return 0;

    const totalDays = completedRequests.reduce((sum: number, request: any) => {
      const created = new Date(request.createdAt);
      const updated = new Date(request.updatedAt);
      const diffDays = Math.ceil((updated.getTime() - created.getTime()) / (1000 * 60 * 60 * 24));
      return sum + diffDays;
    }, 0);

    return Math.round(totalDays / completedRequests.length);
  };

  const getTopPerformingPorts = () => {
    if (!vessels) return [];
    const portCounts = vessels.reduce((acc: any, vessel: any) => {
      const port = vessel.portOfDischarge;
      acc[port] = (acc[port] || 0) + 1;
      return acc;
    }, {});

    return Object.entries(portCounts)
      .sort(([,a], [,b]) => (b as number) - (a as number))
      .slice(0, 5)
      .map(([port, count]) => ({ port, count }));
  };

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  // Check access permissions
  // Reports are now accessible to all authenticated users

  const requestsByStatus = calculateRequestsByStatus();
  const totalLCQuantity = calculateTotalLCQuantity();
  const avgProcessingTime = calculateAverageProcessingTime();
  const topPorts = getTopPerformingPorts();

  return (
    <MainLayout title="Reports" subtitle="Analytics and reporting dashboard">
          {/* Report Controls */}
          <Card className="bg-white border border-secondary-200">
            <CardHeader>
              <CardTitle className="text-xl font-bold text-secondary-900">
                Report Configuration
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div>
                  <Label htmlFor="reportType">Report Type</Label>
                  <Select value={selectedReport} onValueChange={setSelectedReport}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="overview">Overview</SelectItem>
                      <SelectItem value="financial">Financial</SelectItem>
                      <SelectItem value="operational">Operational</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="dateFrom">From Date</Label>
                  <Input
                    id="dateFrom"
                    type="date"
                    value={dateRange.from}
                    onChange={(e) => setDateRange(prev => ({ ...prev, from: e.target.value }))}
                  />
                </div>
                <div>
                  <Label htmlFor="dateTo">To Date</Label>
                  <Input
                    id="dateTo"
                    type="date"
                    value={dateRange.to}
                    onChange={(e) => setDateRange(prev => ({ ...prev, to: e.target.value }))}
                  />
                </div>
                <div className="flex items-end">
                  <Button 
                    onClick={() => exportReport(selectedReport)}
                    className="bg-primary-500 hover:bg-primary-600 text-white w-full"
                  >
                    <Download className="mr-2" size={16} />
                    Export
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          <Tabs value={selectedReport} onValueChange={setSelectedReport}>
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="overview">Overview</TabsTrigger>
              <TabsTrigger value="financial">Financial</TabsTrigger>
              <TabsTrigger value="operational">Operational</TabsTrigger>
            </TabsList>

            {/* Overview Tab */}
            <TabsContent value="overview" className="space-y-6">
              {/* KPI Summary */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-secondary-600">Total Requests</p>
                        <p className="text-3xl font-bold text-secondary-900">
                          {requests?.length || 0}
                        </p>
                      </div>
                      <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                        <FileText className="text-blue-600" size={20} />
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-secondary-600">Total LC Quantity</p>
                        <p className="text-3xl font-bold text-secondary-900">
                          {totalLCQuantity.toLocaleString()} tons
                        </p>
                      </div>
                      <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                        <Package className="text-green-600" size={20} />
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-secondary-600">Active Vessels</p>
                        <p className="text-3xl font-bold text-secondary-900">
                          {vessels?.filter((v: any) => v.status === 'in_transit').length || 0}
                        </p>
                      </div>
                      <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
                        <Ship className="text-orange-600" size={20} />
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-sm font-medium text-secondary-600">Avg. Processing</p>
                        <p className="text-3xl font-bold text-secondary-900">
                          {avgProcessingTime} days
                        </p>
                      </div>
                      <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
                        <Clock className="text-purple-600" size={20} />
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Request Status Breakdown */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <Card className="bg-white border border-secondary-200">
                  <CardHeader>
                    <CardTitle className="text-lg font-bold text-secondary-900">
                      Request Status Breakdown
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      {Object.entries(requestsByStatus).map(([status, count]) => (
                        <div key={status} className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            <Badge className={getStatusColor(status)}>
                              {status.charAt(0).toUpperCase() + status.slice(1).replace('_', ' ')}
                            </Badge>
                          </div>
                          <span className="font-semibold text-secondary-900">{count as number}</span>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardHeader>
                    <CardTitle className="text-lg font-bold text-secondary-900">
                      Top Performing Ports
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      {topPorts.map((port: any, index) => (
                        <div key={port.port} className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                              <span className="text-blue-600 text-sm font-bold">{index + 1}</span>
                            </div>
                            <span className="font-medium text-secondary-900">{port.port}</span>
                          </div>
                          <span className="font-semibold text-secondary-900">{port.count} vessels</span>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              </div>
            </TabsContent>

            {/* Financial Tab */}
            <TabsContent value="financial" className="space-y-6">
              <div className="space-y-6">
                {/* Filters Section */}
                <Card className="bg-white border border-secondary-200">
                <CardHeader>
                  <CardTitle className="text-lg font-bold text-secondary-900 flex items-center">
                    <Filter className="mr-2" size={20} />
                    Filter Letters of Credit
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <Label htmlFor="issuingBank">Issuing Bank</Label>
                      <Select value={selectedIssuingBank} onValueChange={setSelectedIssuingBank}>
                        <SelectTrigger>
                          <SelectValue placeholder="All Banks" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Banks</SelectItem>
                          {getUniqueIssuingBanks().map((bank) => (
                            <SelectItem key={bank} value={bank}>{bank}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="lcStatus">LC Status</Label>
                      <Select value={selectedLCStatus} onValueChange={setSelectedLCStatus}>
                        <SelectTrigger>
                          <SelectValue placeholder="All Statuses" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Statuses</SelectItem>
                          {getUniqueLCStatuses().map((status) => (
                            <SelectItem key={status} value={status}>
                              {status.charAt(0).toUpperCase() + status.slice(1)}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="flex items-end">
                      <Button 
                        variant="outline"
                        onClick={() => {
                          setSelectedIssuingBank("all");
                          setSelectedLCStatus("all");
                        }}
                        className="w-full"
                      >
                        <X className="mr-2" size={16} />
                        Clear Filters
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Quantity Summary */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-secondary-900">
                        {getFilteredLCs().reduce((sum, lc) => sum + lc.totalQuantity, 0).toLocaleString()}
                      </p>
                      <p className="text-sm text-secondary-600">Total Quantity</p>
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-blue-600">
                        {getFilteredLCs().reduce((sum, lc) => sum + lc.allocatedQuantity, 0).toLocaleString()}
                      </p>
                      <p className="text-sm text-secondary-600">Allocated Quantity</p>
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-green-600">
                        {getFilteredLCs().reduce((sum, lc) => sum + Math.max(0, lc.remainingQuantity), 0).toLocaleString()}
                      </p>
                      <p className="text-sm text-secondary-600">Available Quantity</p>
                    </div>
                  </CardContent>
                </Card>

                <Card className="bg-white border border-secondary-200">
                  <CardContent className="p-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-red-600">
                        {Math.abs(getFilteredLCs().reduce((sum, lc) => sum + Math.min(0, lc.remainingQuantity), 0)).toLocaleString()}
                      </p>
                      <p className="text-sm text-secondary-600">Over-allocated</p>
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Letters of Credit Table */}
              <Card className="bg-white border border-secondary-200">
                <CardHeader>
                  <CardTitle className="text-lg font-bold text-secondary-900">
                    Letters of Credit ({getFilteredLCs().length} items)
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {lcsLoading ? (
                    <div className="space-y-4">
                      {[1, 2, 3, 4, 5].map((i) => (
                        <div key={i} className="h-16 bg-secondary-100 rounded-lg animate-pulse" />
                      ))}
                    </div>
                  ) : getFilteredLCs().length > 0 ? (
                    <div className="overflow-x-auto">
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead>LC Number</TableHead>
                            <TableHead>Issuing Bank</TableHead>
                            <TableHead>Advising Bank</TableHead>
                            <TableHead>Total Qty</TableHead>
                            <TableHead>Allocated Qty</TableHead>
                            <TableHead>Remaining Qty</TableHead>
                            <TableHead>Currency</TableHead>
                            <TableHead>Issue Date</TableHead>
                            <TableHead>Expiry Date</TableHead>
                            <TableHead>Status</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {getFilteredLCs().map((lc: any) => (
                            <TableRow key={lc.id}>
                              <TableCell className="font-medium">{lc.lcNumber || 'N/A'}</TableCell>
                              <TableCell>{lc.issuingBank || 'N/A'}</TableCell>
                              <TableCell>{lc.advisingBank || 'N/A'}</TableCell>
                              <TableCell className="font-medium">
                                {lc.totalQuantity.toLocaleString()} tons
                              </TableCell>
                              <TableCell className={lc.allocatedQuantity > 0 ? "text-blue-600 font-medium" : ""}>
                                {lc.allocatedQuantity.toLocaleString()} tons
                              </TableCell>
                              <TableCell className={`font-medium ${
                                lc.remainingQuantity < 0 
                                  ? "text-red-600" 
                                  : lc.remainingQuantity === 0 
                                    ? "text-gray-500" 
                                    : "text-green-600"
                              }`}>
                                {lc.remainingQuantity.toLocaleString()} tons
                                {lc.remainingQuantity < 0 && (
                                  <span className="text-xs ml-1">(Over-allocated)</span>
                                )}
                              </TableCell>
                              <TableCell>{lc.currency || 'USD'}</TableCell>
                              <TableCell>
                                {lc.issueDate ? new Date(lc.issueDate).toLocaleDateString() : 'N/A'}
                              </TableCell>
                              <TableCell>
                                {lc.expiryDate ? new Date(lc.expiryDate).toLocaleDateString() : 'N/A'}
                              </TableCell>
                              <TableCell>
                                <Badge className={getStatusColor(lc.status)}>
                                  {lc.status.charAt(0).toUpperCase() + lc.status.slice(1)}
                                </Badge>
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </div>
                  ) : (
                    <div className="text-center py-12">
                      <DollarSign className="mx-auto h-16 w-16 text-secondary-400 mb-4" />
                      <p className="text-lg font-medium text-secondary-600 mb-2">No LCs Found</p>
                      <p className="text-secondary-500">
                        {selectedIssuingBank !== "all" || selectedLCStatus !== "all" 
                          ? "Try adjusting your filters to see more results."
                          : "No Letters of Credit have been created yet."
                        }
                      </p>
                    </div>
                  )}
                </CardContent>
                </Card>
              </div>
            </TabsContent>

            {/* Operational Tab - Redirects to dedicated Operations page */}
            <TabsContent value="operational" className="space-y-6">
              <Card className="bg-white border border-secondary-200">
                <CardContent className="p-12 text-center">
                  <div className="w-20 h-20 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-6">
                    <Settings className="text-blue-600" size={32} />
                  </div>
                  <h3 className="text-2xl font-bold text-secondary-900 mb-4">
                    Operational Analytics Moved
                  </h3>
                  <p className="text-secondary-600 mb-8 max-w-md mx-auto">
                    Our operational analytics and supplier management features have been moved to a dedicated Operations page 
                    with enhanced interactive charts and filtering capabilities.
                  </p>
                  <Link href="/operations">
                    <Button className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-3 text-lg">
                      <ExternalLink className="mr-2" size={20} />
                      Go to Operations Page
                    </Button>
                  </Link>
                  <div className="mt-8 p-4 bg-blue-50 rounded-lg">
                    <h4 className="font-semibold text-blue-900 mb-2">New Features Available:</h4>
                    <ul className="text-blue-800 text-sm space-y-1">
                      <li>â€¢ Interactive supplier distribution charts</li>
                      <li>â€¢ Click-to-filter vessel tables by supplier</li>
                      <li>â€¢ Enhanced operational KPIs and metrics</li>
                      <li>â€¢ Real-time quantity and performance tracking</li>
                    </ul>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>


          </Tabs>
    </MainLayout>
  );
}

client/src/pages/requests.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import MainLayout from "@/components/layout/main-layout";
import StatementOfNeedsForm from "@/components/forms/statement-of-needs-form";
import DocumentVoting from "@/components/document-voting";
import RequestVoting, { RequestVotingContent, RequestVoteButton } from "@/components/request-voting";
import RequestSummary from "@/components/request-summary";
import StatusChangeDropdown from "@/components/ui/status-change-dropdown";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Plus, FileText, Edit, Trash, Settings, Vote, Eye, MessageSquare } from "lucide-react";

export default function Requests() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const queryClient = useQueryClient();
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [editingRequest, setEditingRequest] = useState<any>(null);
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [showStatusDialog, setShowStatusDialog] = useState(false);
  const [statusEditingRequest, setStatusEditingRequest] = useState<any>(null);
  const [showVotingDialog, setShowVotingDialog] = useState(false);
  const [votingRequest, setVotingRequest] = useState<any>(null);
  const [showSummaryDialog, setShowSummaryDialog] = useState(false);
  const [summaryRequest, setSummaryRequest] = useState<any>(null);

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: requests, isLoading: requestsLoading } = useQuery({
    queryKey: ["/api/requests", statusFilter !== "all" ? { status: statusFilter } : {}],
    retry: false,
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest("DELETE", `/api/requests/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/requests"] });
      toast({
        title: "Success",
        description: "Request deleted successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete request",
        variant: "destructive",
      });
    },
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, status }: { id: number; status: string }) => {
      await apiRequest("PUT", `/api/requests/${id}`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/requests"] });
      toast({
        title: "Success",
        description: "Request status updated successfully",
      });
      setShowStatusDialog(false);
      setStatusEditingRequest(null);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update request status",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending": return "bg-yellow-100 text-yellow-800";
      case "approved": return "bg-green-100 text-green-800";
      case "rejected": return "bg-red-100 text-red-800";
      case "in_progress": return "bg-blue-100 text-blue-800";
      case "completed": return "bg-purple-100 text-purple-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "high": return "bg-red-100 text-red-800";
      case "medium": return "bg-yellow-100 text-yellow-800";
      case "low": return "bg-green-100 text-green-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <MainLayout title="Contract Requests" subtitle="Manage statements of needs and contract requests">
          {/* Header Actions */}
          <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
            <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4 w-full sm:w-auto">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-full sm:w-48">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="approved">Approved</SelectItem>
                  <SelectItem value="rejected">Rejected</SelectItem>
                  <SelectItem value="in_progress">In Progress</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
              <DialogTrigger asChild>
                <Button className="bg-primary-500 hover:bg-primary-600 text-black w-full sm:w-auto">
                  <Plus className="mr-2" size={16} />
                  New Request
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>Create New Import Request</DialogTitle>
                </DialogHeader>
                <StatementOfNeedsForm 
                  onSuccess={() => setShowCreateDialog(false)}
                  onCancel={() => setShowCreateDialog(false)}
                />
              </DialogContent>
            </Dialog>
          </div>

          {/* Edit Request Dialog */}
          <Dialog open={showEditDialog} onOpenChange={setShowEditDialog}>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Edit Contract Request</DialogTitle>
              </DialogHeader>
              {editingRequest && (
                <StatementOfNeedsForm 
                  request={editingRequest}
                  onSuccess={() => {
                    setShowEditDialog(false);
                    setEditingRequest(null);
                  }}
                  onCancel={() => {
                    setShowEditDialog(false);
                    setEditingRequest(null);
                  }}
                />
              )}
            </DialogContent>
          </Dialog>

          {/* Change Status Dialog */}
          <Dialog open={showStatusDialog} onOpenChange={setShowStatusDialog}>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Change Request Status</DialogTitle>
              </DialogHeader>
              {statusEditingRequest && (
                <div className="space-y-4">
                  <div>
                    <p className="text-sm text-gray-600 mb-2">
                      Current status: <Badge className={getStatusColor(statusEditingRequest.status)}>
                        {statusEditingRequest.status.charAt(0).toUpperCase() + statusEditingRequest.status.slice(1)}
                      </Badge>
                    </p>
                    <p className="text-sm font-medium">Request: {statusEditingRequest.title}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium">New Status:</label>
                    <Select 
                      onValueChange={(value) => {
                        updateStatusMutation.mutate({ 
                          id: statusEditingRequest.id, 
                          status: value 
                        });
                      }}
                    >
                      <SelectTrigger className="w-full mt-1">
                        <SelectValue placeholder="Select new status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="pending">Pending</SelectItem>
                        <SelectItem value="approved">Approved</SelectItem>
                        <SelectItem value="rejected">Rejected</SelectItem>
                        <SelectItem value="in_progress">In Progress</SelectItem>
                        <SelectItem value="completed">Completed</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="flex justify-end space-x-2">
                    <Button 
                      variant="outline" 
                      onClick={() => {
                        setShowStatusDialog(false);
                        setStatusEditingRequest(null);
                      }}
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              )}
            </DialogContent>
          </Dialog>

          {/* Document Voting Dialog */}
          <Dialog open={showVotingDialog} onOpenChange={setShowVotingDialog}>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Document Voting - {votingRequest?.title}</DialogTitle>
              </DialogHeader>
              {votingRequest && (
                <DocumentVoting 
                  entityType="request"
                  entityId={votingRequest.id}
                  allowVoting={(user as any)?.role === 'admin' || (user as any)?.role === 'manager'}
                />
              )}
            </DialogContent>
          </Dialog>

          {/* Requests Table */}
          <Card className="bg-white border border-secondary-200">
            <CardHeader>
              <CardTitle className="text-xl font-bold text-secondary-900">
                Contract Requests
              </CardTitle>
            </CardHeader>
            <CardContent>
              {requestsLoading ? (
                <div className="space-y-4">
                  {[1, 2, 3, 4, 5].map((i) => (
                    <div key={i} className="h-16 bg-secondary-100 rounded-lg animate-pulse" />
                  ))}
                </div>
              ) : requests && Array.isArray(requests) && requests.length > 0 ? (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Title</TableHead>
                      <TableHead>Description</TableHead>
                      <TableHead>Supplier</TableHead>
                      <TableHead>Quantity</TableHead>
                      <TableHead>Price per Ton (USD)</TableHead>
                      <TableHead>Priority</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Created</TableHead>
                      <TableHead>Recommend on Doc</TableHead>
                      <TableHead>Request opinions</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {Array.isArray(requests) && requests.map((request: any) => (
                      <TableRow key={request.id}>
                        <TableCell className="font-medium">{request.title}</TableCell>
                        <TableCell className="max-w-xs truncate">
                          {request.description}
                        </TableCell>
                        <TableCell>
                          {request.supplierName || '-'}
                        </TableCell>
                        <TableCell>
                          {request.quantity} {request.unitOfMeasure}
                        </TableCell>
                        <TableCell>
                          ${parseFloat(request.pricePerTon || request.estimatedValue).toLocaleString()}
                        </TableCell>
                        <TableCell>
                          <Badge className={getPriorityColor(request.priority)}>
                            {request.priority.charAt(0).toUpperCase() + request.priority.slice(1)}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          <div className="flex flex-col space-y-2">
                            <Badge className={getStatusColor(request.status)}>
                              {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                            </Badge>
                            {['admin', 'manager', 'procurement_officer'].includes((user as any)?.role || '') && (
                              <StatusChangeDropdown
                                entityType="request"
                                entityId={request.id}
                                currentStatus={request.status}
                              />
                            )}
                          </div>
                        </TableCell>
                        <TableCell>
                          {new Date(request.createdAt).toLocaleDateString()}
                        </TableCell>
                        <TableCell>
                          {request.status !== 'applied' ? (
                            <RequestVoteButton 
                              requestId={request.id}
                              currentUserId={(user as any)?.id}
                            />
                          ) : (
                            <span className="text-gray-500 text-sm">Hidden (status: applied)</span>
                          )}
                        </TableCell>
                        <TableCell>
                          <div className="flex items-center space-x-2">
                            {request.status !== 'applied' ? (
                              <>
                                {/* View Opinions Button - Shows summary and all votes */}
                                <Dialog>
                                  <DialogTrigger asChild>
                                    <Button variant="outline" size="sm" className="flex items-center space-x-2 bg-blue-100 text-blue-700 border-blue-300 hover:bg-blue-200">
                                      <MessageSquare size={14} />
                                      <span>View Opinions</span>
                                    </Button>
                                  </DialogTrigger>

                                  <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                                    <DialogHeader>
                                      <DialogTitle>Import Request Opinions</DialogTitle>
                                    </DialogHeader>
                                    <RequestVotingContent 
                                      requestId={request.id}
                                      currentUserId={(user as any)?.id}
                                    />
                                  </DialogContent>
                                </Dialog>
                              </>
                            ) : (
                              <span className="text-gray-500 text-sm">Hidden (status: applied)</span>
                            )}
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="flex items-center space-x-2">
                            <Button 
                              size="sm" 
                              variant="outline"
                              className="bg-blue-50 hover:bg-blue-100 border-blue-200"
                              onClick={() => {
                                setSummaryRequest(request);
                                setShowSummaryDialog(true);
                              }}
                              title="View request summary"
                            >
                              <Eye size={14} className="mr-1" />
                              View
                            </Button>
                            <Button 
                              size="sm" 
                              className="bg-blue-500 hover:bg-blue-600 text-white"
                              onClick={() => {
                                setEditingRequest(request);
                                setShowEditDialog(true);
                              }}
                            >
                              <Edit size={14} />
                            </Button>
                            {((user as any)?.role === 'admin' || (user as any)?.role === 'procurement_officer') && (
                              <Button 
                                size="sm" 
                                className="bg-green-500 hover:bg-green-600 text-white"
                                onClick={() => {
                                  setStatusEditingRequest(request);
                                  setShowStatusDialog(true);
                                }}
                              >
                                <Settings size={14} />
                              </Button>
                            )}
                            <Button 
                              size="sm" 
                              variant="destructive"
                              onClick={() => {
                                if (window.confirm("Are you sure you want to delete this request?")) {
                                  deleteMutation.mutate(request.id);
                                }
                              }}
                              disabled={deleteMutation.isPending}
                              title="Delete request"
                            >
                              <Trash size={14} />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              ) : (
                <div className="text-center py-12">
                  <FileText className="mx-auto h-12 w-12 text-secondary-400" />
                  <h3 className="mt-4 text-lg font-semibold text-secondary-900">No requests found</h3>
                  <p className="mt-2 text-secondary-600">
                    {statusFilter === "all" 
                      ? "Get started by creating your first contract request." 
                      : `No requests with status "${statusFilter}" found.`}
                  </p>
                  <Button 
                    className="mt-4 bg-primary-500 hover:bg-primary-600 text-black"
                    onClick={() => setShowCreateDialog(true)}
                  >
                    <Plus className="mr-2" size={16} />
                    Create New Request
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Request Summary Dialog */}
          <RequestSummary 
            request={summaryRequest}
            isOpen={showSummaryDialog}
            onClose={() => {
              setShowSummaryDialog(false);
              setSummaryRequest(null);
            }}
          />
    </MainLayout>
  );
}

client/src/pages/shipments.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/layout/sidebar";
import Topbar from "@/components/layout/topbar";
import ShipmentTrackingForm from "@/components/forms/shipment-tracking-form";
import StatusBadge from "@/components/ui/status-badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Plus, Package, Edit, Download, CheckCircle, Ship, Anchor, Clock, Trash2 } from "lucide-react";

export default function Shipments() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const queryClient = useQueryClient();

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: shipments, isLoading: shipmentsLoading } = useQuery({
    queryKey: ["/api/shipments", statusFilter !== "all" ? { status: statusFilter } : {}],
    retry: false,
  });

  const { data: vessels } = useQuery({
    queryKey: ["/api/vessels"],
    retry: false,
  });

  const deleteMutation = useMutation({
    mutationFn: async (shipmentId: number) => {
      await apiRequest(`/api/shipments/${shipmentId}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Shipment deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/shipments"] });
    },
    onError: (error: any) => {
      if (isUnauthorizedError(error)) {
        window.location.href = "/api/login";
        return;
      }
      toast({
        title: "Error",
        description: error.message || "Failed to delete shipment",
        variant: "destructive",
      });
    },
  });

  const handleDeleteShipment = (shipmentId: number) => {
    if (window.confirm("Are you sure you want to delete this shipment? This action cannot be undone.")) {
      deleteMutation.mutate(shipmentId);
    }
  };

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen flex bg-secondary-50">
      <Sidebar />

      <main className="flex-1 ml-64">
        <Topbar title="Shipments" subtitle="Track cargo loading, transit, and discharge operations" />

        <div className="p-6 space-y-6">
          {/* Header Actions */}
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-48">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="loading">Loading</SelectItem>
                  <SelectItem value="loaded">Loaded</SelectItem>
                  <SelectItem value="in_transit">In Transit</SelectItem>
                  <SelectItem value="nor_issued">NOR Issued</SelectItem>
                  <SelectItem value="discharging">Discharging</SelectItem>
                  <SelectItem value="discharged">Discharged</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {['admin', 'shipping_officer'].includes(user?.role) && (
              <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
                <DialogTrigger asChild>
                  <Button className="bg-primary-500 hover:bg-primary-600 text-white">
                    <Plus className="mr-2" size={16} />
                    Track Shipment
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Create New Shipment Tracking</DialogTitle>
                  </DialogHeader>
                  <ShipmentTrackingForm 
                    onSuccess={() => setShowCreateDialog(false)}
                    onCancel={() => setShowCreateDialog(false)}
                    vessels={vessels || []}
                  />
                </DialogContent>
              </Dialog>
            )}
          </div>

          {/* Shipment Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Total Shipments</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {shipments?.length || 0}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                    <Package className="text-blue-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">In Transit</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {shipments?.filter((shipment: any) => shipment.status === 'in_transit').length || 0}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
                    <Ship className="text-orange-600" size={20} />
                  </div>
