              </CardContent>
            </Card>

            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Discharging</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {shipments?.filter((shipment: any) => shipment.status === 'discharging').length || 0}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-yellow-100 rounded-lg flex items-center justify-center">
                    <Anchor className="text-yellow-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Completed</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {shipments?.filter((shipment: any) => shipment.status === 'discharged').length || 0}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                    <CheckCircle className="text-green-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Active Shipments Timeline */}
          <Card className="bg-white border border-secondary-200">
            <CardHeader>
              <CardTitle className="text-xl font-bold text-secondary-900">
                Active Shipments Timeline
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {shipments?.filter((shipment: any) => !['discharged'].includes(shipment.status)).slice(0, 3).map((shipment: any) => {
                  const vessel = vessels?.find((v: any) => v.id === shipment.vesselId);
                  return (
                    <div key={shipment.id} className="border border-secondary-200 rounded-lg p-4">
                      <div className="flex items-center justify-between mb-4">
                        <div>
                          <h3 className="font-semibold text-secondary-900">
                            {vessel?.vesselName || 'Unknown Vessel'}
                          </h3>
                          <p className="text-sm text-secondary-600">
                            BL: {shipment.billOfLadingNumber || 'Pending'}
                          </p>
                        </div>
                        <StatusBadge status={shipment.status} type="shipment" />
                      </div>

                      {/* Mini Timeline */}
                      <div className="flex items-center space-x-4">
                        <div className={`flex flex-col items-center ${
                          ['loaded', 'in_transit', 'nor_issued', 'discharging', 'discharged'].includes(shipment.status) 
                            ? 'text-green-600' 
                            : 'text-secondary-400'
                        }`}>
                          <CheckCircle size={16} />
                          <span className="text-xs mt-1">Loaded</span>
                        </div>
                        <div className={`h-0.5 flex-1 ${
                          ['in_transit', 'nor_issued', 'discharging', 'discharged'].includes(shipment.status)
                            ? 'bg-green-500'
                            : 'bg-secondary-200'
                        }`} />
                        <div className={`flex flex-col items-center ${
                          ['in_transit', 'nor_issued', 'discharging', 'discharged'].includes(shipment.status)
                            ? 'text-green-600'
                            : shipment.status === 'in_transit'
                            ? 'text-blue-600'
                            : 'text-secondary-400'
                        }`}>
                          <Ship size={16} />
                          <span className="text-xs mt-1">Transit</span>
                        </div>
                        <div className={`h-0.5 flex-1 ${
                          ['nor_issued', 'discharging', 'discharged'].includes(shipment.status)
                            ? 'bg-green-500'
                            : 'bg-secondary-200'
                        }`} />
                        <div className={`flex flex-col items-center ${
                          ['nor_issued', 'discharging', 'discharged'].includes(shipment.status)
                            ? 'text-green-600'
                            : 'text-secondary-400'
                        }`}>
                          <Anchor size={16} />
                          <span className="text-xs mt-1">NOR</span>
                        </div>
                        <div className={`h-0.5 flex-1 ${
                          shipment.status === 'discharged'
                            ? 'bg-green-500'
                            : 'bg-secondary-200'
                        }`} />
                        <div className={`flex flex-col items-center ${
                          shipment.status === 'discharged'
                            ? 'text-green-600'
                            : 'text-secondary-400'
                        }`}>
                          <CheckCircle size={16} />
                          <span className="text-xs mt-1">Complete</span>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>

          {/* Shipments Table */}
          <Card className="bg-white border border-secondary-200">
            <CardHeader>
              <CardTitle className="text-xl font-bold text-secondary-900">
                All Shipments
              </CardTitle>
            </CardHeader>
            <CardContent>
              {shipmentsLoading ? (
                <div className="space-y-4">
                  {[1, 2, 3, 4, 5].map((i) => (
                    <div key={i} className="h-16 bg-secondary-100 rounded-lg animate-pulse" />
                  ))}
                </div>
              ) : shipments && shipments.length > 0 ? (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Vessel</TableHead>
                      <TableHead>BL Number</TableHead>
                      <TableHead>Loading Date</TableHead>
                      <TableHead>NOR Date</TableHead>
                      <TableHead>Quantity</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {shipments.map((shipment: any) => {
                      const vessel = vessels?.find((v: any) => v.id === shipment.vesselId);
                      return (
                        <TableRow key={shipment.id}>
                          <TableCell className="font-medium">
                            {vessel?.vesselName || 'Unknown Vessel'}
                          </TableCell>
                          <TableCell>{shipment.billOfLadingNumber || '-'}</TableCell>
                          <TableCell>
                            {shipment.loadingDate 
                              ? new Date(shipment.loadingDate).toLocaleDateString()
                              : '-'
                            }
                          </TableCell>
                          <TableCell>
                            {shipment.norDate 
                              ? new Date(shipment.norDate).toLocaleDateString()
                              : '-'
                            }
                          </TableCell>
                          <TableCell>
                            {shipment.quantityUnloaded 
                              ? `${shipment.quantityUnloaded.toLocaleString()}`
                              : '-'
                            }
                          </TableCell>
                          <TableCell>
                            <StatusBadge status={shipment.status} type="shipment" />
                          </TableCell>
                          <TableCell>
                            <div className="flex items-center space-x-2">
                              {['admin', 'shipping_officer'].includes(user?.role) && (
                                <Button size="sm" variant="outline">
                                  <Edit size={14} />
                                </Button>
                              )}
                              {['admin', 'shipping_officer'].includes(user?.role) && (
                                <Button 
                                  size="sm" 
                                  variant="outline"
                                  onClick={() => handleDeleteShipment(shipment.id)}
                                  className="text-red-600 hover:text-red-700 hover:border-red-300"
                                >
                                  <Trash2 size={14} />
                                </Button>
                              )}
                              {shipment.billOfLadingFile && (
                                <Button size="sm" variant="outline" asChild>
                                  <a href={`/api/files/${shipment.billOfLadingFile}`} target="_blank" rel="noopener noreferrer">
                                    <Download size={14} />
                                  </a>
                                </Button>
                              )}
                              {shipment.norFile && (
                                <Button size="sm" variant="outline" asChild>
                                  <a href={`/api/files/${shipment.norFile}`} target="_blank" rel="noopener noreferrer">
                                    <Download size={14} />
                                  </a>
                                </Button>
                              )}
                            </div>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              ) : (
                <div className="text-center py-12">
                  <Package className="mx-auto h-12 w-12 text-secondary-400" />
                  <h3 className="mt-4 text-lg font-semibold text-secondary-900">No shipments found</h3>
                  <p className="mt-2 text-secondary-600">
                    {statusFilter === "all" 
                      ? "Get started by tracking your first shipment." 
                      : `No shipments with status "${statusFilter}" found.`}
                  </p>
                  {['admin', 'shipping_officer'].includes(user?.role) && (
                    <Button 
                      className="mt-4 bg-primary-500 hover:bg-primary-600 text-white"
                      onClick={() => setShowCreateDialog(true)}
                    >
                      <Plus className="mr-2" size={16} />
                      Track Shipment
                    </Button>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

