import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { isUnauthorizedError } from "@/lib/authUtils";
import MainLayout from "@/components/layout/main-layout";
import VesselNominationForm from "@/components/forms/vessel-nomination-form";
import VesselSummary from "@/components/vessel-summary";
import VesselDischargeTracking from "@/components/vessel-discharge-tracking";
import StatusBadge from "@/components/ui/status-badge";
import StatusChangeDropdown from "@/components/ui/status-change-dropdown";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Plus, Ship, Edit, Download, MapPin, Calendar, Eye, Activity, Trash } from "lucide-react";

// Component to display LC numbers for a vessel
function VesselLCDisplay({ vesselId, lcs }: { vesselId: number; lcs: any[] }) {
  const { data: vesselLCs } = useQuery({
    queryKey: [`/api/vessels/${vesselId}/letters-of-credit`],
    enabled: !!vesselId,
  });

  const vesselLCsArray = Array.isArray(vesselLCs) ? vesselLCs : [];

  if (vesselLCsArray.length === 0) {
    return <span>-</span>;
  }

  return (
    <div className="space-y-1">
      {vesselLCsArray.map((vesselLC: any, index: number) => {
        const lc = lcs.find((l: any) => l.id === vesselLC.lcId);
        return (
          <Badge key={index} variant="secondary" className="text-xs">
            {lc ? lc.lcNumber : `LC-${vesselLC.lcId}`}
          </Badge>
        );
      })}
    </div>
  );
}

export default function Vessels() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const queryClient = useQueryClient();
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [editingVessel, setEditingVessel] = useState<any>(null);
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [viewingVessel, setViewingVessel] = useState<any>(null);
  const [showViewDialog, setShowViewDialog] = useState(false);
  const [trackingVessel, setTrackingVessel] = useState<any>(null);
  const [showTrackingDialog, setShowTrackingDialog] = useState(false);

  // Delete vessel mutation
  const deleteVesselMutation = useMutation({
    mutationFn: async (vesselId: number) => {
      const response = await fetch(`/api/vessels/${vesselId}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        let errorMessage = `${response.status}: ${response.statusText}`;
        try {
          const errorData = await response.json();
          errorMessage = errorData.message || errorMessage;
        } catch {
          // If JSON parsing fails, use status text
        }
        throw new Error(errorMessage);
      }

      // For DELETE requests, the response might be empty or not JSON
      const text = await response.text();
      try {
        return text ? JSON.parse(text) : { success: true };
      } catch {
        return { success: true }; // Assume success if response is not JSON
      }
    },
    onSuccess: () => {
      // Remove all vessel-related queries from cache completely
      queryClient.removeQueries({ queryKey: ["/api/vessels"] });
      queryClient.removeQueries({ predicate: (query) => query.queryKey[0] === "/api/vessels" });
      queryClient.removeQueries({ queryKey: ["/api/dashboard/stats"] });

      // Force immediate refetch
      queryClient.refetchQueries({ queryKey: ["/api/vessels", statusFilter !== "all" ? { status: statusFilter } : {}] });
      queryClient.refetchQueries({ queryKey: ["/api/dashboard/stats"] });

      toast({
        title: "Success",
        description: "Vessel deleted successfully",
      });
    },
    onError: (error) => {
      console.error("Delete vessel error:", error);
      toast({
        title: "Error",
        description: `Failed to delete vessel: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: vessels, isLoading: vesselsLoading } = useQuery({
    queryKey: ["/api/vessels", statusFilter !== "all" ? { status: statusFilter } : {}],
    retry: false,
  });

  const { data: contracts } = useQuery({
    queryKey: ["/api/contracts"],
    retry: false,
  });

  const { data: lcs } = useQuery({
    queryKey: ["/api/letters-of-credit"],
    retry: false,
  });

  const vesselsArray = Array.isArray(vessels) ? vessels : [];
  const contractsArray = Array.isArray(contracts) ? contracts : [];
  const lcsArray = Array.isArray(lcs) ? lcs : [];

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <MainLayout title="Vessels" subtitle="Manage vessel nominations and shipping coordination">
          {/* Header Actions */}
          <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
            <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4 w-full sm:w-auto">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-full sm:w-48">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Statuses</SelectItem>
                  <SelectItem value="nominated">Nominated</SelectItem>
                  <SelectItem value="confirmed">Confirmed</SelectItem>
                  <SelectItem value="in_transit">In Transit</SelectItem>
                  <SelectItem value="arrived">Arrived</SelectItem>
                  <SelectItem value="discharged">Discharged</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
              <DialogTrigger asChild>
                <Button className="bg-primary-500 hover:bg-primary-600 text-black w-full sm:w-auto">
                  <Plus className="mr-2" size={16} />
                  Nominate Vessel
                </Button>
              </DialogTrigger>
                <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Nominate New Vessel</DialogTitle>
                  </DialogHeader>
                  <VesselNominationForm 
                    onSuccess={() => setShowCreateDialog(false)}
                    onCancel={() => setShowCreateDialog(false)}
                    contracts={contractsArray}
                    lcs={lcsArray}
                  />
                </DialogContent>
              </Dialog>

              {/* Edit Vessel Dialog */}
              <Dialog open={showEditDialog} onOpenChange={setShowEditDialog}>
                <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>Edit Vessel</DialogTitle>
                  </DialogHeader>
                  <VesselNominationForm 
                    vessel={editingVessel}
                    onSuccess={() => {
                      setShowEditDialog(false);
                      setEditingVessel(null);
                    }}
                    onCancel={() => {
                      setShowEditDialog(false);
                      setEditingVessel(null);
                    }}
                    contracts={contractsArray}
                    lcs={lcsArray}
                  />
                </DialogContent>
              </Dialog>

              {/* Vessel Summary Dialog */}
              <VesselSummary
                vessel={viewingVessel}
                isOpen={showViewDialog}
                onClose={() => {
                  setShowViewDialog(false);
                  setViewingVessel(null);
                }}
              />

              {/* Discharge Tracking Dialog */}
              <VesselDischargeTracking
                vessel={trackingVessel}
                isOpen={showTrackingDialog}
                onClose={() => {
                  setShowTrackingDialog(false);
                  setTrackingVessel(null);
                }}
              />
          </div>

          {/* Vessel Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Total Vessels</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {vesselsArray.length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                    <Ship className="text-blue-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">In Transit</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {vesselsArray.filter((vessel: any) => vessel.status === 'in_transit').length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
                    <Ship className="text-orange-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Arriving Soon</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {vesselsArray.filter((vessel: any) => {
                        if (!vessel.eta) return false;
                        const eta = new Date(vessel.eta);
                        const now = new Date();
                        const diffDays = Math.ceil((eta.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
                        return diffDays <= 7 && diffDays > 0;
                      }).length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                    <Calendar className="text-green-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-white border border-secondary-200">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Completed</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {vesselsArray.filter((vessel: any) => vessel.status === 'discharged').length}
                    </p>
                  </div>
                  <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center">
                    <Ship className="text-purple-600" size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Vessels Table */}
          <Card className="bg-white border border-secondary-200">
            <CardHeader>
              <CardTitle className="text-xl font-bold text-secondary-900">
                Vessels
              </CardTitle>
            </CardHeader>
            <CardContent>
              {vesselsLoading ? (
                <div className="space-y-4">
                  {[1, 2, 3, 4, 5].map((i) => (
                    <div key={i} className="h-16 bg-secondary-100 rounded-lg animate-pulse" />
                  ))}
                </div>
              ) : vesselsArray.length > 0 ? (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Vessel Name</TableHead>
                      <TableHead>Contract ID</TableHead>
                      <TableHead>LC Number</TableHead>
                      <TableHead>Quantity</TableHead>
                      <TableHead>Route</TableHead>
                      <TableHead>ETA</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Customs Release</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {vesselsArray.map((vessel: any) => (
                      <TableRow key={vessel.id}>
                        <TableCell className="font-medium">{vessel.vesselName || 'Unnamed Vessel'}</TableCell>
                        <TableCell>CON-{vessel.contractId ? vessel.contractId.toString().padStart(3, '0') : '000'}</TableCell>
                        <TableCell>
                          <VesselLCDisplay vesselId={vessel.id} lcs={lcsArray} />
                        </TableCell>
                        <TableCell>{vessel.quantity || 0} tons</TableCell>
                        <TableCell>
                          <div className="space-y-1">
                            <div className="flex items-center space-x-1 text-sm">
                              <MapPin size={12} className="text-secondary-500" />
                              <span>{vessel.portOfLoading || 'TBD'}</span>
                            </div>
                            <div className="flex items-center space-x-1 text-sm text-secondary-600">
                              <span>â†’</span>
                              <span>{vessel.portOfDischarge || 'TBD'}</span>
                            </div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="space-y-1">
                            <div className="text-sm font-medium">
                              {vessel.eta ? new Date(vessel.eta).toLocaleDateString() : 'TBD'}
                            </div>
                            <div className="text-xs text-secondary-500">
                              {vessel.eta ? new Date(vessel.eta).toLocaleTimeString() : ''}
                            </div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="flex items-center space-x-2">
                            <StatusBadge status={vessel.status} type="vessel" />
                            <StatusChangeDropdown
                              entityType="vessel"
                              entityId={vessel.id}
                              currentStatus={vessel.status}
                            />
                          </div>
                        </TableCell>
                        <TableCell>
                          <Badge className={
                            vessel?.customsReleaseStatus === 'verified' ? 'bg-green-100 text-green-800' :
                            vessel?.customsReleaseStatus === 'received' ? 'bg-blue-100 text-blue-800' :
                            'bg-yellow-100 text-yellow-800'
                          }>
                            {vessel?.customsReleaseStatus === 'verified' ? 'Released' :
                             vessel?.customsReleaseStatus === 'received' ? 'Received' :
                             'Pending'}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          <div className="flex items-center space-x-2">
                            <Button 
                              size="sm" 
                              variant="outline"
                              className="bg-blue-50 hover:bg-blue-100 border-blue-200"
                              onClick={() => {
                                setViewingVessel(vessel);
                                setShowViewDialog(true);
                              }}
                              title="View vessel summary"
                            >
                              <Eye size={14} className="mr-1" />
                              View
                            </Button>
                            <Button 
                              size="sm" 
                              className="bg-blue-500 hover:bg-blue-600 text-white"
                              onClick={() => {
                                setEditingVessel(vessel);
                                setShowEditDialog(true);
                              }}
                              title="Edit vessel details"
                            >
                              <Edit size={14} />
                            </Button>
                            {vessel.status !== 'draft' && ['admin', 'shipping_officer'].includes((user as any)?.role || '') && (
                              <Button 
                                size="sm" 
                                className="bg-orange-500 hover:bg-orange-600 text-white"
                                onClick={() => {
                                  setTrackingVessel(vessel);
                                  setShowTrackingDialog(true);
                                }}
                                title="Track discharge progress"
                              >
                                <Activity size={14} />
                              </Button>
                            )}
                            {vessel.instructionsFile && (
                              <Button size="sm" variant="outline" asChild>
                                <a href={`/api/files/${vessel.instructionsFile}`} target="_blank" rel="noopener noreferrer">
                                  <Download size={14} />
                                </a>
                              </Button>
                            )}
                            <Button 
                              size="sm" 
                              variant="destructive"
                              onClick={() => {
                                if (window.confirm("Are you sure you want to delete this vessel?")) {
                                  deleteVesselMutation.mutate(vessel.id);
                                }
                              }}
                              disabled={deleteVesselMutation.isPending}
                              title="Delete vessel"
                            >
                              <Trash size={14} />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              ) : (
                <div className="text-center py-12">
                  <Ship className="mx-auto h-12 w-12 text-secondary-400" />
                  <h3 className="mt-4 text-lg font-semibold text-secondary-900">No vessels found</h3>
                  <p className="mt-2 text-secondary-600">
                    {statusFilter === "all" 
                      ? "Get started by nominating your first vessel." 
                      : `No vessels with status "${statusFilter}" found.`}
                  </p>
                  {['admin', 'shipping_officer'].includes((user as any)?.role || '') && (
                    <Button 
                      className="mt-4 bg-primary-500 hover:bg-primary-600 text-white"
                      onClick={() => setShowCreateDialog(true)}
                    >
                      <Plus className="mr-2" size={16} />
                      Nominate Vessel
                    </Button>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
    </MainLayout>
  );
}

client/src/components/forms/contract-for.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { insertContractSchema } from "@shared/schema";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import FileUpload from "@/components/ui/file-upload";
import { CARGO_TYPES, COUNTRIES } from "@/lib/constants";

// Create a more flexible schema for drafts
const contractFormSchema = z.object({
  requestId: z.coerce.number().min(1, "Please select a request"),
  supplierName: z.string().min(1, "Supplier name is required"),
  quantity: z.coerce.number().min(1, "Quantity must be at least 1").optional().or(z.literal("")),
  cargoType: z.string().min(1, "Cargo type is required"),
  countryOfOrigin: z.string().optional(),
  incoterms: z.string().min(1, "Incoterms is required"),
  contractTerms: z.string().optional(),
  startDate: z.string().optional(),
  endDate: z.string().optional(),
  reviewNotes: z.string().optional(),
  status: z.string().default("draft"),
  createdBy: z.string().optional(),
})
.refine(
  (data) => {
    // File upload is mandatory - check if files are selected
    return true; // We'll validate this in the component
  },
  {
    message: "Contract document is required",
    path: ["uploadedFile"]
  }
);

type ContractFormData = z.infer<typeof contractFormSchema>;

interface ContractFormProps {
  onSuccess?: () => void;
  onCancel?: () => void;
  onDelete?: () => void;
  requests: any[];
  contract?: any;
}

export default function ContractForm({ onSuccess, onCancel, onDelete, requests, contract }: ContractFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch
  } = useForm<ContractFormData>({
    resolver: zodResolver(contractFormSchema),
    defaultValues: contract ? {
      ...contract,
      quantity: contract.quantity ? parseInt(contract.quantity) : 0,
      startDate: contract.startDate ? new Date(contract.startDate).toISOString().split('T')[0] : '',
      endDate: contract.endDate ? new Date(contract.endDate).toISOString().split('T')[0] : '',
    } : {
      status: "draft"
    }
  });

  const createMutation = useMutation({
    mutationFn: async (data: ContractFormData) => {
      const formData = new FormData();

      // Add form fields - ensure required fields are present
      Object.entries(data).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          formData.append(key, value.toString());
        }
      });

      // Add file if selected
      if (selectedFiles.length > 0) {
        formData.append('contractFile', selectedFiles[0]);
      }

      const url = contract ? `/api/contracts/${contract.id}` : "/api/contracts";
      const method = contract ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contracts"] });
      toast({
        title: "Success",
        description: contract ? "Contract updated successfully" : "Contract created successfully",
      });
      onSuccess?.();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: contract ? "Failed to update contract" : "Failed to create contract",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async () => {
      if (!contract?.id) throw new Error("Contract ID is required");

      const response = await fetch(`/api/contracts/${contract.id}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contracts"] });
      toast({
        title: "Success",
        description: "Contract deleted successfully",
      });
      onDelete?.();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete contract",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ContractFormData) => {
    // Validate mandatory file upload
    if (selectedFiles.length === 0 && !contract?.uploadedFile) {
      toast({
        title: "Contract Document Required",
        description: "Please upload a contract document",
        variant: "destructive",
      });
      return;
    }

    console.log("Form submitted with data:", data);
    createMutation.mutate(data);
  };

  return (
    <Card>
      <CardContent className="p-6">
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label htmlFor="requestId">Related Request *</Label>
              <Select 
                onValueChange={(value) => setValue("requestId", parseInt(value))}
                defaultValue={contract?.requestId?.toString()}
              >
                <SelectTrigger className={errors.requestId ? "border-red-500" : ""}>
                  <SelectValue placeholder="Select a request" />
                </SelectTrigger>
                <SelectContent>
                  {requests.map((request) => (
                    <SelectItem key={request.id} value={request.id.toString()}>
                      REQ-{request.id.toString().padStart(3, '0')} - {request.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.requestId && (
                <p className="text-sm text-red-500 mt-1">{errors.requestId.message}</p>
              )}
            </div>
            <div>
              <Label htmlFor="status">Contract Status</Label>
              <Select 
                onValueChange={(value) => setValue("status", value)}
                defaultValue={contract?.status || "draft"}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="draft">Draft</SelectItem>
                  <SelectItem value="under_review">Under Review</SelectItem>
                  <SelectItem value="approved">Approved</SelectItem>
                  <SelectItem value="rejected">Rejected</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label htmlFor="supplierName">Supplier/Vendor Name *</Label>
              <Input
                id="supplierName"
                {...register("supplierName")}
                placeholder="Company name"
                className={errors.supplierName ? "border-red-500" : ""}
              />
              {errors.supplierName && (
                <p className="text-sm text-red-500 mt-1">{errors.supplierName.message}</p>
              )}
            </div>
            <div>
              <Label htmlFor="cargoType">Cargo Type *</Label>
              <Select 
                onValueChange={(value) => setValue("cargoType", value)}
                defaultValue={contract?.cargoType}
              >
                <SelectTrigger className={errors.cargoType ? "border-red-500" : ""}>
                  <SelectValue placeholder="Select cargo type" />
                </SelectTrigger>
                <SelectContent>
                  {CARGO_TYPES.map((cargo) => (
                    <SelectItem key={cargo.value} value={cargo.value}>{cargo.label}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.cargoType && (
                <p className="text-sm text-red-500 mt-1">{errors.cargoType.message}</p>
              )}
            </div>
            <div>
              <Label htmlFor="countryOfOrigin">Country of Origin</Label>
              <Select 
                onValueChange={(value) => setValue("countryOfOrigin", value)}
                defaultValue={contract?.countryOfOrigin}
              >
                <SelectTrigger className={errors.countryOfOrigin ? "border-red-500" : ""}>
                  <SelectValue placeholder="Select country of origin" />
                </SelectTrigger>
                <SelectContent>
                  {COUNTRIES.map((country) => (
                    <SelectItem key={country} value={country}>{country}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.countryOfOrigin && (
                <p className="text-sm text-red-500 mt-1">{errors.countryOfOrigin.message}</p>
              )}
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label htmlFor="incoterms">Incoterms *</Label>
              <Select 
                onValueChange={(value) => setValue("incoterms", value)}
                defaultValue={contract?.incoterms}
              >
                <SelectTrigger className={errors.incoterms ? "border-red-500" : ""}>
                  <SelectValue placeholder="Select incoterms" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="CIF">CIF - Cost, Insurance & Freight</SelectItem>
                  <SelectItem value="FOB">FOB - Free on Board</SelectItem>
                  <SelectItem value="DDP">DDP - Delivered Duty Paid</SelectItem>
                  <SelectItem value="DAP">DAP - Delivered at Place</SelectItem>
                  <SelectItem value="EXW">EXW - Ex Works</SelectItem>
                  <SelectItem value="FCA">FCA - Free Carrier</SelectItem>
                </SelectContent>
              </Select>
              {errors.incoterms && (
                <p className="text-sm text-red-500 mt-1">{errors.incoterms.message}</p>
              )}
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-1 gap-6">
            <div>
              <Label htmlFor="quantity">Quantity *</Label>
              <Input
                id="quantity"
                type="number"
                step="1"
                {...register("quantity", { valueAsNumber: true })}
                placeholder="e.g. 1000"
                className={errors.quantity ? "border-red-500" : ""}
              />
              {errors.quantity && (
                <p className="text-sm text-red-500 mt-1">{errors.quantity.message}</p>
              )}
            </div>
          </div>

          <div>
            <Label>Contract Document *</Label>
            <FileUpload
              accept=".pdf,.doc,.docx"
              onFileSelect={setSelectedFiles}
              existingFile={contract?.uploadedFile}
              description="PDF, DOC, DOCX up to 10MB (Required)"
            />
            {selectedFiles.length === 0 && !contract?.uploadedFile && (
              <p className="text-sm text-red-500 mt-1">Contract document is required</p>
            )}
          </div>

          <div>
            <Label htmlFor="contractTerms">Contract Terms & Conditions</Label>
            <Textarea
              id="contractTerms"
              {...register("contractTerms")}
              rows={4}
              placeholder="Key terms, payment conditions, delivery terms, etc."
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label htmlFor="startDate">Import Duration - Start Date</Label>
              <Input
                id="startDate"
                type="date"
                {...register("startDate")}
                placeholder="Select start date"
              />
            </div>
            <div>
              <Label htmlFor="endDate">Import Duration - End Date</Label>
              <Input
                id="endDate"
                type="date"
                {...register("endDate")}
                placeholder="Select end date"
              />
            </div>
          </div>

          <div>
            <Label htmlFor="reviewNotes">Review Notes</Label>
            <Textarea
              id="reviewNotes"
              {...register("reviewNotes")}
              rows={3}
              placeholder="Administrative notes and comments"
            />
          </div>

          <div className="flex justify-between">
            <div className="flex space-x-3">
              {contract && (
                <Button 
                  type="button" 
                  variant="destructive"
                  onClick={() => deleteMutation.mutate()}
                  disabled={deleteMutation.isPending}
                >
                  {deleteMutation.isPending ? "Deleting..." : "Delete Contract"}
                </Button>
              )}
            </div>
            <div className="flex space-x-3">
              {onCancel && (
                <Button type="button" variant="outline" onClick={onCancel}>
                  Cancel
                </Button>
              )}
              <Button 
                type="button" 
                variant="outline"
                onClick={() => {
                  console.log("Save Draft clicked");
                  console.log("Form errors:", errors);
                  setValue("status", "draft");
                  handleSubmit(onSubmit)();
                }}
                disabled={createMutation.isPending}
              >
                Save Draft
              </Button>
              <Button 
                type="submit" 
                className="bg-green-500 hover:bg-green-600 text-white"
                disabled={createMutation.isPending}
              >
                {createMutation.isPending ? "Saving..." : (contract ? "Update Contract" : "Create Contract")}
              </Button>
            </div>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/forms/final-settle.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { insertFinalSettlementSchema } from "@shared/schema";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import FileUpload from "@/components/ui/file-upload";

const settlementFormSchema = insertFinalSettlementSchema.extend({
  settlementDate: z.string(),
});

type SettlementFormData = z.infer<typeof settlementFormSchema>;

interface FinalSettlementFormProps {
  onSuccess?: () => void;
  onCancel?: () => void;
  requests: any[];
  settlement?: any;
}

export default function FinalSettlementForm({ onSuccess, onCancel, requests, settlement }: FinalSettlementFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch
  } = useForm<SettlementFormData>({
    resolver: zodResolver(settlementFormSchema),
    defaultValues: settlement ? {
      ...settlement,
      settlementDate: settlement.settlementDate ? new Date(settlement.settlementDate).toISOString().split('T')[0] : '',
    } : {
      status: "pending"
    }
  });

  const createMutation = useMutation({
    mutationFn: async (data: SettlementFormData) => {
      const formData = new FormData();

      // Add form fields
      Object.entries(data).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          formData.append(key, value.toString());
        }
      });

      // Add file if selected
      if (selectedFiles.length > 0) {
        formData.append('documentsFile', selectedFiles[0]);
      }

      const url = settlement ? `/api/final-settlements/${settlement.id}` : "/api/final-settlements";
      const method = settlement ? "PUT" : "POST";

      const response = await fetch(url, {
        method,
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/final-settlements"] });
      toast({
        title: "Success",
        description: settlement ? "Settlement updated successfully" : "Settlement created successfully",
      });
      onSuccess?.();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: settlement ? "Failed to update settlement" : "Failed to create settlement",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: SettlementFormData) => {
    createMutation.mutate(data);
  };

  // Calculate totals
  const watchedValues = watch();
  const calculateTotal = () => {
    const costs = [
      'goodsCost',
      'shippingCost', 
      'insurance',
      'portCharges',
      'customsDuties',
      'otherFees'
    ];

    return costs.reduce((total, field) => {
      const value = parseFloat(watchedValues[field as keyof typeof watchedValues] as string || '0');
      return total + (isNaN(value) ? 0 : value);
    }, 0);
  };

  return (
    <Card>
      <CardContent className="p-6">
        {/* Financial Summary */}
        <Card className="bg-secondary-50 mb-6">
          <CardHeader>
            <CardTitle className="text-lg">Financial Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div>
                <p className="text-sm text-secondary-600">Total Calculated</p>
                <p className="text-2xl font-bold text-secondary-900">
                  ${calculateTotal().toLocaleString()}
                </p>
              </div>
              <div>
                <p className="text-sm text-secondary-600">Final Invoice Amount</p>
                <p className="text-2xl font-bold text-green-600">
                  ${parseFloat(watchedValues.finalInvoiceAmount || '0').toLocaleString()}
                </p>
              </div>
              <div>
                <p className="text-sm text-secondary-600">Difference</p>
                <p className="text-2xl font-bold text-orange-600">
                  ${(parseFloat(watchedValues.finalInvoiceAmount || '0') - calculateTotal()).toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label htmlFor="requestId">Related Request *</Label>
              <select 
                {...register("requestId", { valueAsNumber: true })}
                className="w-full px-4 py-3 border border-secondary-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
              >
                <option value="">Select a request</option>
                {requests.map((request) => (
                  <option key={request.id} value={request.id}>
                    REQ-{request.id.toString().padStart(3, '0')} - {request.title}
                  </option>
                ))}
              </select>
              {errors.requestId && (
                <p className="text-sm text-red-500 mt-1">{errors.requestId.message}</p>
              )}
            </div>
            <div>
              <Label htmlFor="settlementDate">Settlement Date *</Label>
              <Input
                id="settlementDate"
                type="date"
                {...register("settlementDate")}
                className={errors.settlementDate ? "border-red-500" : ""}
              />
              {errors.settlementDate && (
                <p className="text-sm text-red-500 mt-1">{errors.settlementDate.message}</p>
              )}
            </div>
          </div>

          <div>
            <Label htmlFor="finalInvoiceAmount">Final Invoice Amount (USD) *</Label>
            <Input
              id="finalInvoiceAmount"
              type="number"
              step="0.01"
              {...register("finalInvoiceAmount")}
              placeholder="235750"
              className={errors.finalInvoiceAmount ? "border-red-500" : ""}
            />
            {errors.finalInvoiceAmount && (
              <p className="text-sm text-red-500 mt-1">{errors.finalInvoiceAmount.message}</p>
            )}
          </div>

          {/* Cost Breakdown */}
          <Card className="border border-secondary-200">
            <CardHeader>
              <CardTitle className="text-lg">Cost Breakdown</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="goodsCost">Goods Cost</Label>
                  <Input
                    id="goodsCost"
                    type="number"
                    step="0.01"
                    {...register("goodsCost")}
                    placeholder="200000"
                  />
                </div>
                <div>
                  <Label htmlFor="shippingCost">Shipping Cost</Label>
                  <Input
                    id="shippingCost"
                    type="number"
                    step="0.01"
                    {...register("shippingCost")}
                    placeholder="15000"
                  />
                </div>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="insurance">Insurance</Label>
                  <Input
                    id="insurance"
                    type="number"
                    step="0.01"
                    {...register("insurance")}
                    placeholder="2500"
                  />
                </div>
                <div>
                  <Label htmlFor="portCharges">Port Charges</Label>
                  <Input
                    id="portCharges"
                    type="number"
                    step="0.01"
                    {...register("portCharges")}
                    placeholder="3250"
                  />
                </div>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="customsDuties">Customs Duties</Label>
                  <Input
                    id="customsDuties"
                    type="number"
                    step="0.01"
                    {...register("customsDuties")}
                    placeholder="12000"
                  />
                </div>
                <div>
                  <Label htmlFor="otherFees">Other Fees</Label>
                  <Input
                    id="otherFees"
                    type="number"
                    step="0.01"
                    {...register("otherFees")}
                    placeholder="3000"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          <div>
            <Label htmlFor="settlementNotes">Settlement Notes</Label>
            <Textarea
              id="settlementNotes"
              {...register("settlementNotes")}
              rows={4}
              placeholder="Final settlement notes, discrepancies, adjustments..."
            />
          </div>

          <div>
            <Label>Settlement Documents</Label>
            <FileUpload
              accept=".pdf"
              multiple
              onFileSelect={setSelectedFiles}
              existingFile={settlement?.documentsFile}
              description="PDF files up to 10MB each"
            />
          </div>

          <div className="flex justify-end space-x-3">
            {onCancel && (
              <Button type="button" variant="outline" onClick={onCancel}>
                Cancel
              </Button>
            )}
            <Button 
              type="button" 
              variant="outline"
              onClick={() => setValue("status", "pending")}
              disabled={createMutation.isPending}
            >
              Save Draft
            </Button>
            <Button 
              type="submit" 
              className="bg-green-500 hover:bg-green-600 text-white"
              disabled={createMutation.isPending}
            >
              {createMutation.isPending ? "Processing..." : "Complete Settlement"}
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

