    const baseQuery = db.select().from(approvalWorkflows);
    const query = documentType 
      ? baseQuery.where(eq(approvalWorkflows.documentType, documentType))
      : baseQuery;

    return await query.orderBy(desc(approvalWorkflows.createdAt));
  }

