import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Calendar, DollarSign, Package, Truck, User, FileText, ExternalLink, Ship } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import StatusBadge from "@/components/ui/status-badge";

interface ContractSummaryProps {
  contract: any;
  isOpen: boolean;
  onClose: () => void;
}

export default function ContractSummary({ contract, isOpen, onClose }: ContractSummaryProps) {
  if (!contract) return null;

  // Fetch vessels associated with this contract
  const { data: vessels } = useQuery({
    queryKey: ["/api/vessels"],
    enabled: isOpen && !!contract.id,
  });

  // Filter vessels for this contract
  const contractVessels = vessels ? (vessels as any[]).filter(vessel => vessel.contractId === contract.id) : [];

  // Calculate total vessel quantities and remaining
  const totalVesselQuantity = contractVessels.reduce((sum, vessel) => sum + (vessel.quantity || 0), 0);
  const remainingQuantity = (contract.quantity || 0) - totalVesselQuantity;

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'approved': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      case 'draft': return 'bg-gray-100 text-gray-800';
      case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <FileText className="h-5 w-5" />
            <span>Contract Summary - CON-{contract.id?.toString().padStart(3, '0')}</span>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Status */}
          <div className="flex items-center space-x-4">
            <Badge className={getStatusColor(contract.status)}>
              {contract.status?.charAt(0).toUpperCase() + contract.status?.slice(1)}
            </Badge>
            {contract.createdAt && (
              <div className="flex items-center space-x-1 text-sm text-gray-600">
                <Calendar className="h-4 w-4" />
                <span>Created: {new Date(contract.createdAt).toLocaleDateString()}</span>
              </div>
            )}
          </div>

          {/* Basic Information */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Contract Details</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <h4 className="font-semibold text-gray-900">Supplier</h4>
                <p className="text-gray-700">{contract.supplierName}</p>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">


                <div className="flex items-center space-x-2">
                  <Package className="h-4 w-4 text-gray-500" />
                  <div>
                    <span className="text-sm font-medium text-gray-500">Cargo Type:</span>
                    <p className="text-gray-900">{contract.cargoType}</p>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Truck className="h-4 w-4 text-gray-500" />
                  <div>
                    <span className="text-sm font-medium text-gray-500">Quantity:</span>
                    <p className="text-gray-900">{contract.quantity} tons</p>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Package className="h-4 w-4 text-gray-500" />
                  <div>
                    <span className="text-sm font-medium text-gray-500">Incoterms:</span>
                    <p className="text-gray-900">{contract.incoterms}</p>
                  </div>
                </div>
              </div>

              {contract.contractTerms && (
                <div>
                  <h4 className="font-semibold text-gray-900">Contract Terms</h4>
                  <p className="text-gray-700">{contract.contractTerms}</p>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Contract Document */}
          {contract.uploadedFile && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg flex items-center space-x-2">
                  <FileText className="h-5 w-5" />
                  <span>Contract Document</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center space-x-2">
                    <FileText className="h-5 w-5 text-blue-600" />
                    <span className="font-medium">{contract.uploadedFile}</span>
                    <Badge variant="outline">
                      {contract.documentStatus || 'Active'}
                    </Badge>
                  </div>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      const link = document.createElement('a');
                      link.href = `/uploads/${contract.uploadedFile}`;
                      link.target = '_blank';
                      link.click();
                    }}
                    className="bg-blue-100 text-blue-700 border-blue-300 hover:bg-blue-200"
                  >
                    <ExternalLink size={14} className="mr-1" />
                    View Document
                  </Button>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Contract Dates */}
          {(contract.startDate || contract.endDate) && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Import Duration</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {contract.startDate && (
                    <div className="flex items-center space-x-2">
                      <Calendar className="h-4 w-4 text-gray-500" />
                      <div>
                        <span className="text-sm font-medium text-gray-500">Import Start Date:</span>
                        <p className="text-gray-900">{new Date(contract.startDate).toLocaleDateString()}</p>
                      </div>
                    </div>
                  )}
                  {contract.endDate && (
                    <div className="flex items-center space-x-2">
                      <Calendar className="h-4 w-4 text-gray-500" />
                      <div>
                        <span className="text-sm font-medium text-gray-500">Import End Date:</span>
                        <p className="text-gray-900">{new Date(contract.endDate).toLocaleDateString()}</p>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Created By */}
          {contract.createdByUser && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Created By</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center space-x-2">
                  <User className="h-4 w-4 text-gray-500" />
                  <span className="font-medium">
                    {contract.createdByUser.firstName} {contract.createdByUser.lastName}
                  </span>
                  <Badge variant="outline">{contract.createdByUser.role}</Badge>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Associated Vessels */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg flex items-center space-x-2">
                <Ship className="h-5 w-5" />
                <span>Associated Vessels</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {/* Summary Stats */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <div className="text-sm font-medium text-blue-600">Contract Quantity</div>
                    <div className="text-xl font-bold text-blue-900">{contract.quantity || 0} tons</div>
                  </div>
                  <div className="bg-green-50 p-4 rounded-lg">
                    <div className="text-sm font-medium text-green-600">Allocated</div>
                    <div className="text-xl font-bold text-green-900">{totalVesselQuantity} tons</div>
                  </div>
                  <div className={`p-4 rounded-lg ${remainingQuantity > 0 ? 'bg-orange-50' : 'bg-gray-50'}`}>
                    <div className={`text-sm font-medium ${remainingQuantity > 0 ? 'text-orange-600' : 'text-gray-600'}`}>
                      Remaining
                    </div>
                    <div className={`text-xl font-bold ${remainingQuantity > 0 ? 'text-orange-900' : 'text-gray-900'}`}>
                      {remainingQuantity} tons
                    </div>
                  </div>
                </div>

                {/* Vessels Table */}
                {contractVessels.length > 0 ? (
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Vessel Name</TableHead>
                        <TableHead>Quantity</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>ETA</TableHead>
                        <TableHead>Route</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {contractVessels.map((vessel: any) => (
                        <TableRow key={vessel.id}>
                          <TableCell className="font-medium">{vessel.vesselName}</TableCell>
                          <TableCell>{vessel.quantity || 0} tons</TableCell>
                          <TableCell>
                            <StatusBadge status={vessel.status} type="vessel" />
                          </TableCell>
                          <TableCell>
                            {vessel.eta ? new Date(vessel.eta).toLocaleDateString() : '-'}
                          </TableCell>
                          <TableCell>
                            <div className="text-sm">
                              {vessel.portOfLoading} â†’ {vessel.portOfDischarge}
                            </div>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    <Ship className="mx-auto h-12 w-12 text-gray-300 mb-2" />
                    <p>No vessels nominated for this contract yet.</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Review Notes */}
          {contract.reviewNotes && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Review Notes</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-gray-700">{contract.reviewNotes}</p>
              </CardContent>
            </Card>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/contract-voting.ts.content:
import { useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { CheckCircle, XCircle, MessageSquare, User, Calendar, Vote } from 'lucide-react';

interface ContractVote {
  id: number;
  contractId: number;
  userId: string;
  vote: 'yes' | 'no';
  comment?: string;
  createdAt: string;
  user?: {
    firstName: string;
    lastName: string;
  };
}

interface ContractVotingContentProps {
  contractId: number;
  currentUserId?: string;
}

interface ContractVoteButtonProps {
  contractId: number;
  currentUserId?: string;
}

// Component for just the voting content (used in dialog)
export function ContractVotingContent({ contractId, currentUserId }: ContractVotingContentProps) {
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const [showVoteDialog, setShowVoteDialog] = useState(false);
  const [voteType, setVoteType] = useState<'yes' | 'no'>('yes');
  const [comment, setComment] = useState('');

  // Fetch existing votes for this contract
  const { data: votesData, isLoading, refetch } = useQuery({
    queryKey: [`/api/contracts/${contractId}/votes`],
    queryFn: async () => {
      const response = await apiRequest('GET', `/api/contracts/${contractId}/votes`);
      return response.json();
    },
    refetchOnWindowFocus: true,
    staleTime: 0, // Always refetch to get latest votes
  });

  const votes: ContractVote[] = votesData || [];

  // Check if current user has already voted
  const userVote = votes.find((vote: ContractVote) => vote.userId === currentUserId);
  const canVote = !userVote; // One vote per user

  const submitVoteMutation = useMutation({
    mutationFn: async ({ vote, comment }: { vote: string; comment?: string }) => {
      const response = await apiRequest('POST', `/api/contracts/${contractId}/votes`, {
        vote,
        comment: comment || undefined,
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Opinion Submitted",
        description: "Your opinion has been recorded successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/contracts/${contractId}/votes`] });
      queryClient.invalidateQueries({ queryKey: ["/api/contracts"] });
      setShowVoteDialog(false);
      setComment('');
      refetch();
    },
    onError: (error: any) => {
      const errorMessage = error?.message || "Failed to submit opinion";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleSubmitVote = () => {
    submitVoteMutation.mutate({
      vote: voteType,
      comment: comment.trim() || undefined,
    });
  };

  const yesVotes = votes.filter(vote => vote.vote === 'yes');
  const noVotes = votes.filter(vote => vote.vote === 'no');

  return (
    <div className="space-y-6">
      {/* Recommendation Statistics */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg flex items-center space-x-2">
            <MessageSquare className="h-5 w-5" />
            <span>Contract Opinions Summary</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center space-x-6 mb-4">
            <div className="flex items-center space-x-2">
              <CheckCircle className="h-5 w-5 text-green-600" />
              <span className="font-medium">Approved: {yesVotes.length}</span>
            </div>
            <div className="flex items-center space-x-2">
              <XCircle className="h-5 w-5 text-red-600" />
              <span className="font-medium">Rejected: {noVotes.length}</span>
            </div>
            <div>
              <span className="text-sm text-gray-600">Total Opinions: {votes.length}</span>
            </div>
          </div>
        </CardContent>
      </Card>



      {/* Current User's Vote */}
      {userVote && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Your Opinion</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center space-x-2 mb-2">
              {userVote.vote === 'yes' ? (
                <CheckCircle className="h-5 w-5 text-green-600" />
              ) : (
                <XCircle className="h-5 w-5 text-red-600" />
              )}
              <span className="font-medium">
                {userVote.vote === 'yes' ? 'Approved' : 'Rejected'}
              </span>
              <span className="text-sm text-gray-500">
                on {new Date(userVote.createdAt).toLocaleDateString()}
              </span>
            </div>
            {userVote.comment && (
              <p className="text-gray-700 mt-2">{userVote.comment}</p>
            )}
          </CardContent>
        </Card>
      )}

      {/* All Recommendations */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">All Opinions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {votes.length === 0 ? (
              <p className="text-gray-500 text-center py-4">No opinions submitted yet.</p>
            ) : (
              votes.map((vote: ContractVote) => (
                <div key={vote.id} className="border-l-4 border-gray-200 pl-4 py-2">
                  <div className="flex items-center space-x-2 mb-1">
                    {vote.vote === 'yes' ? (
                      <CheckCircle className="h-4 w-4 text-green-600" />
                    ) : (
                      <XCircle className="h-4 w-4 text-red-600" />
                    )}
                    <span className="font-medium">
                      {vote.user?.firstName} {vote.user?.lastName}
                    </span>
                    <span className="text-sm text-gray-500">
                      {vote.vote === 'yes' ? 'approved' : 'rejected'}
                    </span>
                    <span className="text-xs text-gray-400">
                      {new Date(vote.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  {vote.comment && (
                    <p className="text-gray-700 text-sm ml-6">{vote.comment}</p>
                  )}
                </div>
              ))
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

// Standalone Vote Button Component
export function ContractVoteButton({ contractId, currentUserId }: ContractVoteButtonProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [voteType, setVoteType] = useState<'yes' | 'no'>('yes');
  const [comment, setComment] = useState('');

  // Check if user has already voted
  const { data: votesData } = useQuery({
    queryKey: [`/api/contracts/${contractId}/votes`],
    queryFn: async () => {
      const response = await apiRequest('GET', `/api/contracts/${contractId}/votes`);
      return response.json();
    },
  });

  const votes: ContractVote[] = votesData || [];
  const userVote = votes.find((vote: ContractVote) => vote.userId === currentUserId);
  const canVote = !userVote;

  const submitVoteMutation = useMutation({
    mutationFn: async ({ vote, comment }: { vote: string; comment?: string }) => {
      const response = await apiRequest('POST', `/api/contracts/${contractId}/votes`, {
        vote,
        comment: comment || undefined,
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Opinion Submitted",
        description: "Your opinion has been recorded successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/contracts/${contractId}/votes`] });
      queryClient.invalidateQueries({ queryKey: ["/api/contracts"] });
      setComment('');
    },
    onError: (error: any) => {
      const errorMessage = error?.message || "Failed to submit opinion";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleSubmitVote = () => {
    if (voteType === 'no' && !comment.trim()) {
      toast({
        title: "Comment Required",
        description: "Please provide a reason when rejecting a contract.",
        variant: "destructive",
      });
      return;
    }

    submitVoteMutation.mutate({
      vote: voteType,
      comment: comment.trim() || undefined,
    });
  };

  if (!canVote) {
    return (
      <Button 
        size="sm" 
        variant="outline" 
        disabled
        className="bg-gray-100 text-gray-500"
      >
        <Vote size={14} className="mr-1" />
        Recommended
      </Button>
    );
  }

  return (
    <Dialog>
      <DialogTrigger asChild>
        <Button 
          size="sm" 
          className="bg-green-600 hover:bg-green-700 text-white"
        >
          <Vote size={14} className="mr-1" />
          Recommend
        </Button>
      </DialogTrigger>

      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Recommend on Contract</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div>
            <Label className="text-base font-medium">Your Opinion</Label>
            <div className="flex space-x-4 mt-2">
              <Button
                variant={voteType === 'yes' ? 'default' : 'outline'}
                onClick={() => setVoteType('yes')}
                className={voteType === 'yes' ? 'bg-green-600 hover:bg-green-700' : ''}
              >
                <CheckCircle className="h-4 w-4 mr-2" />
                Approve
              </Button>
              <Button
                variant={voteType === 'no' ? 'default' : 'outline'}
                onClick={() => setVoteType('no')}
                className={voteType === 'no' ? 'bg-red-600 hover:bg-red-700' : ''}
              >
                <XCircle className="h-4 w-4 mr-2" />
                Reject
              </Button>
            </div>
          </div>

          <div>
            <Label htmlFor="comment" className="text-base font-medium">
              Comment {voteType === 'no' && <span className="text-red-500">*</span>}
            </Label>
            <Textarea
              id="comment"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder={voteType === 'no' ? "Please provide a reason for rejection..." : "Add your comments (optional)"}
              className="mt-2"
              rows={3}
            />
          </div>

          <div className="flex space-x-3">
            <Button
              onClick={handleSubmitVote}
              disabled={submitVoteMutation.isPending}
              className="bg-blue-600 hover:bg-blue-700"
            >
              {submitVoteMutation.isPending ? "Submitting..." : "Submit Recommendation"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/document-voting.ts.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { CheckCircle, XCircle, FileText, MessageSquare, Clock } from "lucide-react";

interface DocumentVotingProps {
  entityType: string;
  entityId: number;
  allowVoting?: boolean; // Whether current user can vote
}

export default function DocumentVoting({ entityType, entityId, allowVoting = true }: DocumentVotingProps) {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [votingDocumentId, setVotingDocumentId] = useState<number | null>(null);
  const [voteType, setVoteType] = useState<'approve' | 'reject' | null>(null);
  const [comment, setComment] = useState("");
  const [showVoteDialog, setShowVoteDialog] = useState(false);

  const { data: documentVotes = [], isLoading } = useQuery({
    queryKey: [`/api/document-votes/${entityType}/${entityId}`],
    retry: false,
  });

  const voteMutation = useMutation({
    mutationFn: async ({ documentId, vote, comment }: { documentId: number; vote: string; comment?: string }) => {
      await apiRequest("POST", `/api/document-votes/${documentId}/vote`, {
        vote,
        comment,
        entityType,
        entityId,
        fileName: `${entityType}_document`,
        filePath: `uploads/${entityType}_${entityId}`,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/document-votes/${entityType}/${entityId}`] });
      setShowVoteDialog(false);
      setComment("");
      setVotingDocumentId(null);
      setVoteType(null);
      toast({
        title: "Vote submitted",
        description: "Your vote has been recorded successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit vote",
        variant: "destructive",
      });
    },
  });

  const handleVote = (documentId: number, vote: 'approve' | 'reject') => {
    setVotingDocumentId(documentId);
    setVoteType(vote);

    if (vote === 'reject') {
      setShowVoteDialog(true);
    } else {
      // For approve votes, submit immediately
      voteMutation.mutate({ documentId, vote });
    }
  };

  const submitVote = () => {
    if (!votingDocumentId || !voteType) return;

    if (voteType === 'reject' && !comment.trim()) {
      toast({
        title: "Comment required",
        description: "Please provide a reason for rejecting the document",
        variant: "destructive",
      });
      return;
    }

    voteMutation.mutate({
      documentId: votingDocumentId,
      vote: voteType,
      comment: comment.trim() || undefined,
    });
  };

  const getVoteStatusBadge = (votes: any[]) => {
    const approvals = votes.filter(v => v.vote === 'approve').length;
    const rejections = votes.filter(v => v.vote === 'reject').length;
    const pending = votes.filter(v => v.vote === 'pending').length;

    if (rejections > 0) {
      return <Badge variant="destructive" className="flex items-center space-x-1">
        <XCircle size={12} />
        <span>Rejected</span>
      </Badge>;
    }

    if (pending > 0) {
      return <Badge variant="secondary" className="flex items-center space-x-1">
        <Clock size={12} />
        <span>Pending Review</span>
      </Badge>;
    }

    if (approvals > 0) {
      return <Badge variant="default" className="flex items-center space-x-1 bg-green-600">
        <CheckCircle size={12} />
        <span>Approved</span>
      </Badge>;
    }

    return <Badge variant="outline">No votes</Badge>;
  };

  const getUserVote = (votes: any[]) => {
    return votes?.find(v => v.userId === (user as any)?.id);
  };

  const hasUserVoted = (votes: any[]) => {
    const userVote = getUserVote(votes);
    return userVote && userVote.vote !== 'pending';
  };

  if (isLoading) {
    return <div className="text-center p-4">Loading documents...</div>;
  }

  if (!Array.isArray(documentVotes) || documentVotes.length === 0) {
    return (
      <Card>
        <CardContent className="p-6 text-center text-secondary-600">
          <FileText size={48} className="mx-auto mb-4 opacity-50" />
          <p>No documents uploaded for voting</p>
        </CardContent>
      </Card>
    );
  }

  // Group votes by document
  const documentGroups = (documentVotes as any[]).reduce((acc: any, vote: any) => {
    const key = `${vote.fileName}_${vote.filePath}`;
    if (!acc[key]) {
      acc[key] = {
        fileName: vote.fileName,
        filePath: vote.filePath,
        uploadedBy: vote.uploadedBy,
        createdAt: vote.createdAt,
        votes: [],
      };
    }
    acc[key].votes.push(vote);
    return acc;
  }, {});

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold">Document Approval</h3>

      {Object.values(documentGroups).map((docGroup: any, index) => {
        const userVoted = hasUserVoted(docGroup.votes);
        const userVote = getUserVote(docGroup.votes);

        return (
          <Card key={index}>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center space-x-2 text-base">
                  <FileText size={16} />
                  <span>{docGroup.fileName}</span>
                </CardTitle>
                {getVoteStatusBadge(docGroup.votes)}
              </div>
              <p className="text-sm text-secondary-600">
                Uploaded {new Date(docGroup.createdAt).toLocaleDateString()}
              </p>
            </CardHeader>

            <CardContent>
              {/* Recommendation Actions */}
              {allowVoting && !userVoted && (
                <div className="flex space-x-3 mb-4">
                  <Button
                    onClick={() => handleVote(docGroup.votes[0].id, 'approve')}
                    disabled={voteMutation.isPending}
                    className="bg-green-600 hover:bg-green-700 text-white"
                  >
                    <CheckCircle size={16} className="mr-2" />
                    Approve
                  </Button>
                  <Button
                    onClick={() => handleVote(docGroup.votes[0].id, 'reject')}
                    disabled={voteMutation.isPending}
                    variant="destructive"
                  >
                    <XCircle size={16} className="mr-2" />
                    Reject
                  </Button>
                </div>
              )}

              {/* User's Current Vote */}
              {userVoted && (
                <div className="mb-4 p-3 bg-secondary-50 rounded-lg">
                  <p className="text-sm font-medium">
                    Your vote: {userVote.vote === 'approve' ? 'Approved' : 'Rejected'}
                  </p>
                  {userVote.comment && (
                    <p className="text-sm text-secondary-600 mt-1">
                      Comment: {userVote.comment}
                    </p>
                  )}
                </div>
              )}

              {/* All Votes Summary */}
              <div className="text-sm">
                <p className="font-medium mb-2">Voting Summary:</p>
                <div className="grid grid-cols-3 gap-4 text-center">
                  <div>
                    <p className="text-green-600 font-semibold">
                      {docGroup.votes.filter((v: any) => v.vote === 'approve').length}
                    </p>
                    <p className="text-xs text-secondary-600">Approved</p>
                  </div>
                  <div>
                    <p className="text-red-600 font-semibold">
                      {docGroup.votes.filter((v: any) => v.vote === 'reject').length}
                    </p>
                    <p className="text-xs text-secondary-600">Rejected</p>
                  </div>
                  <div>
                    <p className="text-yellow-600 font-semibold">
                      {docGroup.votes.filter((v: any) => v.vote === 'pending').length}
                    </p>
                    <p className="text-xs text-secondary-600">Pending</p>
                  </div>
                </div>
              </div>

              {/* Comments from rejections */}
              {docGroup.votes.some((v: any) => v.vote === 'reject' && v.comment) && (
                <div className="mt-4 p-3 bg-red-50 rounded-lg">
                  <p className="text-sm font-medium text-red-800 mb-2">Rejection Comments:</p>
                  {docGroup.votes
                    .filter((v: any) => v.vote === 'reject' && v.comment)
                    .map((vote: any, idx: number) => (
                      <div key={idx} className="text-sm text-red-700 mb-1">
                        â€¢ {vote.comment}
                      </div>
                    ))}
                </div>
              )}
            </CardContent>
          </Card>
        );
      })}

      {/* Rejection Comment Dialog */}
      <Dialog open={showVoteDialog} onOpenChange={setShowVoteDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Document</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="rejectComment">Please provide a reason for rejection *</Label>
              <Textarea
                id="rejectComment"
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Explain why you're rejecting this document..."
                rows={4}
              />
            </div>
            <div className="flex justify-end space-x-3">
              <Button 
                variant="outline" 
                onClick={() => setShowVoteDialog(false)}
              >
                Cancel
              </Button>
              <Button 
                variant="destructive" 
                onClick={submitVote}
                disabled={voteMutation.isPending || !comment.trim()}
              >
                Submit Rejection
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/lc-summary.tsx.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Calendar, DollarSign, Package, Truck, User, FileText, ExternalLink, Ship, University } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import StatusBadge from "@/components/ui/status-badge";

interface LCSummaryProps {
  lc: any;
  isOpen: boolean;
  onClose: () => void;
}

export default function LCSummary({ lc, isOpen, onClose }: LCSummaryProps) {
  if (!lc) return null;

  // Fetch vessels associated with this LC via the junction table
  const { data: vessels } = useQuery({
    queryKey: ["/api/vessels"],
    enabled: isOpen && !!lc.id,
  });

  // Fetch LC-vessel relationships
  const { data: lcVesselRelations, isLoading: lcVesselRelationsLoading } = useQuery({
    queryKey: [`/api/letters-of-credit/${lc.id}/vessels`],
    enabled: isOpen && !!lc.id,
  });

  // Debug logging
  console.log("LC ID:", lc.id);
  console.log("LC vessel relations:", lcVesselRelations);
  console.log("Vessels data:", vessels);

  // Get vessels that are associated with this LC
  const lcVessels = vessels && lcVesselRelations ? 
    (vessels as any[]).filter(vessel => 
      (lcVesselRelations as any[]).some(relation => relation.vesselId === vessel.id)
    ) : [];

  // Calculate total vessel quantities and remaining based on the relationship quantities
  const totalVesselQuantity = lcVesselRelations ? 
    (lcVesselRelations as any[]).reduce((sum, relation) => sum + (relation.quantity || 0), 0) : 0;
  const remainingQuantity = (lc.quantity || 0) - totalVesselQuantity;

  console.log("LC vessels filtered:", lcVessels);
  console.log("Total vessel quantity:", totalVesselQuantity);
  console.log("Remaining quantity:", remainingQuantity);

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'draft': return 'bg-gray-100 text-gray-800';
      case 'issued': return 'bg-green-100 text-green-800';
      case 'expired': return 'bg-red-100 text-red-800';
      case 'cancelled': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const isExpired = (expiryDate: string) => {
    if (!expiryDate) return false;
    return new Date(expiryDate) < new Date();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <University className="h-5 w-5" />
            <span>Letter of Credit Summary - {lc.lcNumber}</span>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Status */}
          <div className="flex items-center space-x-4">
            <Badge className={getStatusColor(lc.status)}>
              {lc.status?.charAt(0).toUpperCase() + lc.status?.slice(1)}
            </Badge>
            {isExpired(lc.expiryDate) && (
              <Badge className="bg-red-100 text-red-800">
                Expired
              </Badge>
            )}
            {lc.createdAt && (
              <div className="flex items-center space-x-1 text-sm text-gray-600">
                <Calendar className="h-4 w-4" />
                <span>Created: {new Date(lc.createdAt).toLocaleDateString()}</span>
              </div>
            )}
          </div>

          {/* Basic Information */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">LC Details</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <h4 className="font-semibold text-gray-900">LC Number</h4>
                  <p className="text-gray-700">{lc.lcNumber}</p>
                </div>

                <div className="flex items-center space-x-2">
                  <DollarSign className="h-4 w-4 text-gray-500" />
                  <div>
                    <span className="text-sm font-medium text-gray-500">Unit Price:</span>
                    <p className="text-gray-900">${parseFloat(lc.unitPrice || 0).toLocaleString()} {lc.currency} per ton</p>
                  </div>
                </div>

                <div>
                  <h4 className="font-semibold text-gray-900">Issuing Bank</h4>
                  <p className="text-gray-700">{lc.issuingBank || '-'}</p>
                </div>

                <div>
                  <h4 className="font-semibold text-gray-900">Advising Bank</h4>
                  <p className="text-gray-700">{lc.advisingBank || '-'}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* LC Dates */}
          {(lc.issueDate || lc.expiryDate) && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Important Dates</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {lc.issueDate && (
                    <div className="flex items-center space-x-2">
                      <Calendar className="h-4 w-4 text-gray-500" />
                      <div>
                        <span className="text-sm font-medium text-gray-500">Issue Date:</span>
                        <p className="text-gray-900">{new Date(lc.issueDate).toLocaleDateString()}</p>
                      </div>
                    </div>
                  )}
                  {lc.expiryDate && (
                    <div className="flex items-center space-x-2">
                      <Calendar className="h-4 w-4 text-gray-500" />
                      <div>
                        <span className="text-sm font-medium text-gray-500">Expiry Date:</span>
                        <p className={`${isExpired(lc.expiryDate) ? 'text-red-600' : 'text-gray-900'}`}>
                          {new Date(lc.expiryDate).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Terms and Conditions */}
          {lc.termsConditions && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Terms & Conditions</CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-gray-700 whitespace-pre-wrap">{lc.termsConditions}</p>
              </CardContent>
            </Card>
          )}

          {/* Associated Vessels */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg flex items-center space-x-2">
                <Ship className="h-5 w-5" />
                <span>Associated Vessels</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {/* Summary Stats */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <div className="text-sm font-medium text-blue-600">LC Quantity</div>
                    <div className="text-xl font-bold text-blue-900">{lc.quantity || 0} tons</div>
                  </div>
                  <div className="bg-green-50 p-4 rounded-lg">
                    <div className="text-sm font-medium text-green-600">Allocated</div>
                    <div className="text-xl font-bold text-green-900">{totalVesselQuantity} tons</div>
                  </div>
                  <div className={`p-4 rounded-lg ${remainingQuantity > 0 ? 'bg-orange-50' : 'bg-gray-50'}`}>
                    <div className={`text-sm font-medium ${remainingQuantity > 0 ? 'text-orange-600' : 'text-gray-600'}`}>
                      Remaining
                    </div>
                    <div className={`text-xl font-bold ${remainingQuantity > 0 ? 'text-orange-900' : 'text-gray-900'}`}>
                      {remainingQuantity} tons
                    </div>
                  </div>
                </div>

                {/* Vessels Table */}
                {lcVessels.length > 0 ? (
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Vessel Name</TableHead>
                        <TableHead>Quantity</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>ETA</TableHead>
                        <TableHead>Route</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {lcVessels.map((vessel: any) => {
                        const vesselLCRelation = (lcVesselRelations as any[])?.find(rel => rel.vesselId === vessel.id);
                        return (
                          <TableRow key={vessel.id}>
                            <TableCell className="font-medium">{vessel.vesselName}</TableCell>
                            <TableCell>{vesselLCRelation?.quantity || 0} tons</TableCell>
                            <TableCell>
                              <StatusBadge status={vessel.status} type="vessel" />
                            </TableCell>
                            <TableCell>
                              {vessel.eta ? new Date(vessel.eta).toLocaleDateString() : '-'}
                            </TableCell>
                            <TableCell>
                              <div className="text-sm">
                                {vessel.countryOfOrigin} â†’ {vessel.portOfDischarge}
                              </div>
                            </TableCell>
                          </TableRow>
                        );
                      })}
                    </TableBody>
                  </Table>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    <Ship className="mx-auto h-12 w-12 text-gray-300 mb-2" />
                    <p>No vessels nominated for this LC yet.</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Document */}
          {lc.uploadedFile && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Uploaded Document</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
