import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Calendar, TrendingDown, TrendingUp, CheckCircle, FileText, Upload } from "lucide-react";

const dischargeTrackingSchema = z.object({
  arrivalDate: z.string().optional(),
  dischargeStartDate: z.string().optional(),
  dischargeEndDate: z.string().optional(),
  actualQuantity: z.number().optional(),
  customsReleaseDate: z.string().optional(),
  customsReleaseNumber: z.string().optional(),
  customsReleaseStatus: z.enum(["pending", "received", "verified"]).optional(),
});

type DischargeTrackingData = z.infer<typeof dischargeTrackingSchema>;

interface VesselDischargeTrackingProps {
  vessel: any;
  isOpen: boolean;
  onClose: () => void;
}

export default function VesselDischargeTracking({ vessel, isOpen, onClose }: VesselDischargeTrackingProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [customsReleaseFile, setCustomsReleaseFile] = useState<File | null>(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
    reset
  } = useForm<DischargeTrackingData>({
    resolver: zodResolver(dischargeTrackingSchema),
    defaultValues: {
      arrivalDate: vessel?.arrivalDate ? new Date(vessel.arrivalDate).toISOString().slice(0, 16) : '',
      dischargeStartDate: vessel?.dischargeStartDate ? new Date(vessel.dischargeStartDate).toISOString().slice(0, 16) : '',
      dischargeEndDate: vessel?.dischargeEndDate ? new Date(vessel.dischargeEndDate).toISOString().slice(0, 16) : '',
      actualQuantity: vessel?.actualQuantity || undefined,
      customsReleaseDate: vessel?.customsReleaseDate ? new Date(vessel.customsReleaseDate).toISOString().slice(0, 16) : '',
      customsReleaseNumber: vessel?.customsReleaseNumber || '',
      customsReleaseStatus: vessel?.customsReleaseStatus || 'pending',
    }
  });

  const updateMutation = useMutation({
    mutationFn: async (data: DischargeTrackingData) => {
      const formData = new FormData();

      // Add discharge tracking fields
      if (data.arrivalDate) formData.append('arrivalDate', data.arrivalDate);
      if (data.dischargeStartDate) formData.append('dischargeStartDate', data.dischargeStartDate);
      if (data.dischargeEndDate) formData.append('dischargeEndDate', data.dischargeEndDate);
      if (data.actualQuantity !== undefined) formData.append('actualQuantity', data.actualQuantity.toString());

      // Add customs release fields
      if (data.customsReleaseDate) formData.append('customsReleaseDate', data.customsReleaseDate);
      if (data.customsReleaseNumber) formData.append('customsReleaseNumber', data.customsReleaseNumber);
      if (data.customsReleaseStatus) formData.append('customsReleaseStatus', data.customsReleaseStatus);

      // Add customs release file if uploaded
      if (customsReleaseFile) {
        formData.append('customsReleaseFile', customsReleaseFile);
      }

      const response = await fetch(`/api/vessels/${vessel.id}`, {
        method: "PUT",
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/vessels"] });
      toast({
        title: "Success",
        description: "Discharge tracking updated successfully",
      });
      onClose();
    },
    onError: (error) => {
      console.error("Error updating discharge tracking:", error);
      toast({
        title: "Error",
        description: "Failed to update discharge tracking",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: DischargeTrackingData) => {
    updateMutation.mutate(data);
  };

  const watchedActualQuantity = watch("actualQuantity");
  const plannedQuantity = vessel?.quantity || 0;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Calendar className="h-5 w-5" />
            <span>Discharge Tracking - {vessel?.vesselName}</span>
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Vessel Info Summary */}
          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-4">
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="font-medium text-blue-700">Vessel:</span>
                  <p className="text-blue-900">{vessel?.vesselName}</p>
                </div>
                <div>
                  <span className="font-medium text-blue-700">Planned Quantity:</span>
                  <p className="text-blue-900">{plannedQuantity} tons</p>
                </div>
                <div>
                  <span className="font-medium text-blue-700">Route:</span>
                  <p className="text-blue-900">{vessel?.portOfLoading} â†’ {vessel?.portOfDischarge}</p>
                </div>
                <div>
                  <span className="font-medium text-blue-700">ETA:</span>
                  <p className="text-blue-900">
                    {vessel?.eta ? new Date(vessel.eta).toLocaleDateString() : 'Not set'}
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Discharge Tracking Form */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Dates Section */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Key Dates</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="arrivalDate">Actual Arrival Date</Label>
                  <Input
                    id="arrivalDate"
                    type="datetime-local"
                    {...register("arrivalDate")}
                    className={errors.arrivalDate ? "border-red-500" : ""}
                  />
                  {errors.arrivalDate && (
                    <p className="text-sm text-red-500 mt-1">{errors.arrivalDate.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="dischargeStartDate">Discharge Start Date</Label>
                  <Input
                    id="dischargeStartDate"
                    type="datetime-local"
                    {...register("dischargeStartDate")}
                    className={errors.dischargeStartDate ? "border-red-500" : ""}
                  />
                  {errors.dischargeStartDate && (
                    <p className="text-sm text-red-500 mt-1">{errors.dischargeStartDate.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="dischargeEndDate">Discharge End Date</Label>
                  <Input
                    id="dischargeEndDate"
                    type="datetime-local"
                    {...register("dischargeEndDate")}
                    className={errors.dischargeEndDate ? "border-red-500" : ""}
                  />
                  {errors.dischargeEndDate && (
                    <p className="text-sm text-red-500 mt-1">{errors.dischargeEndDate.message}</p>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Quantity Section */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Quantity Tracking</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="actualQuantity">Actual Quantity Discharged (tons)</Label>
                  <Input
                    id="actualQuantity"
                    type="number"
                    {...register("actualQuantity", { valueAsNumber: true })}
                    placeholder="Enter actual discharged quantity"
                    className={errors.actualQuantity ? "border-red-500" : ""}
                  />
                  {errors.actualQuantity && (
                    <p className="text-sm text-red-500 mt-1">{errors.actualQuantity.message}</p>
                  )}
                </div>

                {/* Real-time Quantity Analysis */}
                <div className="space-y-3">
                  <div className="grid grid-cols-2 gap-3">
                    <div className="text-center p-3 bg-blue-50 rounded-lg border">
                      <span className="text-sm font-medium text-blue-700">Planned</span>
                      <p className="text-xl font-bold text-blue-900">{plannedQuantity} tons</p>
                    </div>

                    <div className="text-center p-3 bg-green-50 rounded-lg border">
                      <span className="text-sm font-medium text-green-700">Actual</span>
                      <p className="text-xl font-bold text-green-900">
                        {watchedActualQuantity || 0} tons
                      </p>
                    </div>
                  </div>

                  {/* Variance Analysis */}
                  {watchedActualQuantity && plannedQuantity && (
                    <div className="mt-3">
                      {watchedActualQuantity < plannedQuantity ? (
                        <div className="p-3 bg-red-50 border border-red-200 rounded-lg">
                          <div className="flex items-center space-x-2">
                            <TrendingDown className="h-4 w-4 text-red-600" />
                            <span className="text-sm font-medium text-red-700">Shortfall:</span>
                            <span className="text-lg font-bold text-red-900">
                              {plannedQuantity - watchedActualQuantity} tons
                            </span>
                          </div>
                          <p className="text-xs text-red-600 mt-1">
                            {(((plannedQuantity - watchedActualQuantity) / plannedQuantity) * 100).toFixed(1)}% below planned
                          </p>
                        </div>
                      ) : watchedActualQuantity > plannedQuantity ? (
                        <div className="p-3 bg-green-50 border border-green-200 rounded-lg">
                          <div className="flex items-center space-x-2">
                            <TrendingUp className="h-4 w-4 text-green-600" />
                            <span className="text-sm font-medium text-green-700">Excess:</span>
                            <span className="text-lg font-bold text-green-900">
                              {watchedActualQuantity - plannedQuantity} tons
                            </span>
                          </div>
                          <p className="text-xs text-green-600 mt-1">
                            {(((watchedActualQuantity - plannedQuantity) / plannedQuantity) * 100).toFixed(1)}% above planned
                          </p>
                        </div>
                      ) : (
                        <div className="p-3 bg-gray-50 border border-gray-200 rounded-lg">
                          <div className="flex items-center space-x-2">
                            <CheckCircle className="h-4 w-4 text-gray-600" />
                            <span className="text-sm font-medium text-gray-700">Perfect Match</span>
                          </div>
                          <p className="text-xs text-gray-600 mt-1">Actual matches planned quantity exactly</p>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Customs Release Documentation */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg flex items-center space-x-2">
                <FileText className="h-5 w-5" />
                <span>Customs Release Documentation</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="customsReleaseDate">Customs Release Date</Label>
                  <Input
                    id="customsReleaseDate"
                    type="datetime-local"
                    {...register("customsReleaseDate")}
                    className={errors.customsReleaseDate ? "border-red-500" : ""}
                  />
                  {errors.customsReleaseDate && (
                    <p className="text-sm text-red-500 mt-1">{errors.customsReleaseDate.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="customsReleaseNumber">Customs Release Number</Label>
                  <Input
                    id="customsReleaseNumber"
                    type="text"
                    {...register("customsReleaseNumber")}
                    placeholder="Enter customs release document number"
                    className={errors.customsReleaseNumber ? "border-red-500" : ""}
                  />
                  {errors.customsReleaseNumber && (
                    <p className="text-sm text-red-500 mt-1">{errors.customsReleaseNumber.message}</p>
                  )}
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="customsReleaseStatus">Customs Release Status</Label>
                  <Select 
                    value={watch("customsReleaseStatus") || "pending"} 
                    onValueChange={(value) => register("customsReleaseStatus").onChange({ target: { value } })}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="pending">Pending</SelectItem>
                      <SelectItem value="received">Document Received</SelectItem>
                      <SelectItem value="verified">Verified & Released</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="customsReleaseFile">Upload Customs Release Document</Label>
                  <div className="flex items-center space-x-2">
                    <Input
                      id="customsReleaseFile"
                      type="file"
                      accept=".pdf,.doc,.docx"
                      onChange={(e) => setCustomsReleaseFile(e.target.files?.[0] || null)}
                      className="flex-1"
                    />
                    {vessel?.customsReleaseFile && (
                      <Button
                        type="button"
                        variant="outline"
                        size="sm"
                        onClick={() => window.open(`/uploads/${vessel.customsReleaseFile}`, '_blank')}
                      >
                        View Current
                      </Button>
                    )}
                  </div>
                  {customsReleaseFile && (
                    <p className="text-sm text-green-600 mt-1">
                      New file selected: {customsReleaseFile.name}
                    </p>
                  )}
                </div>
              </div>

              {/* Customs Release Status Badge */}
              <div className="mt-4 p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-700">Current Status:</span>
                  <Badge className={
                    vessel?.customsReleaseStatus === 'verified' ? 'bg-green-100 text-green-800' :
                    vessel?.customsReleaseStatus === 'received' ? 'bg-blue-100 text-blue-800' :
                    'bg-yellow-100 text-yellow-800'
                  }>
                    {vessel?.customsReleaseStatus === 'verified' ? 'Verified & Released' :
                     vessel?.customsReleaseStatus === 'received' ? 'Document Received' :
                     'Pending Release'}
                  </Badge>
                </div>
                {vessel?.customsReleaseNumber && (
                  <p className="text-sm text-gray-600 mt-1">
                    Release Number: {vessel.customsReleaseNumber}
                  </p>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Action Buttons */}
          <div className="flex justify-end space-x-3 pt-4 border-t">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button 
              type="submit" 
              className="bg-primary-500 hover:bg-primary-600 text-white"
              disabled={updateMutation.isPending}
            >
              {updateMutation.isPending ? "Updating..." : "Update Discharge Tracking"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/vessel-documents.t.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { FileText, Upload, Download, Trash2, Plus, ExternalLink } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

const documentUploadSchema = z.object({
  documentType: z.string().min(1, "Document type is required"),
  documentName: z.string().min(1, "Document name is required"),
  notes: z.string().optional(),
});

type DocumentUploadData = z.infer<typeof documentUploadSchema>;

const DOCUMENT_TYPES = [
  { value: "bill_of_lading", label: "Bill of Lading" },
  { value: "commercial_invoice", label: "Commercial Invoice" },
  { value: "packing_list", label: "Packing List" },
  { value: "certificate_origin", label: "Certificate of Origin" },
  { value: "insurance_certificate", label: "Insurance Certificate" },
  { value: "quality_certificate", label: "Quality Certificate" },
  { value: "phytosanitary", label: "Phytosanitary Certificate" },
  { value: "customs_release", label: "Customs Release Document" },
  { value: "inspection_report", label: "Inspection Report" },
  { value: "weight_certificate", label: "Weight Certificate" },
  { value: "fumigation_certificate", label: "Fumigation Certificate" },
  { value: "shipping_instructions", label: "Shipping Instructions" },
  { value: "other", label: "Other Document" },
];

interface VesselDocumentsProps {
  vessel: any;
}

export default function VesselDocuments({ vessel }: VesselDocumentsProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showUploadDialog, setShowUploadDialog] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    setValue,
    watch
  } = useForm<DocumentUploadData>({
    resolver: zodResolver(documentUploadSchema),
  });

  // Fetch vessel documents
  const { data: documents = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/vessels", vessel.id, "documents"],
    enabled: !!vessel.id,
  });

  // Upload document mutation
  const uploadMutation = useMutation({
    mutationFn: async (data: DocumentUploadData) => {
      if (!selectedFile) {
        throw new Error("No file selected");
      }

      const formData = new FormData();
      formData.append('document', selectedFile);
      formData.append('documentType', data.documentType);
      formData.append('documentName', data.documentName);
      if (data.notes) {
        formData.append('notes', data.notes);
      }

      const response = await fetch(`/api/vessels/${vessel.id}/documents`, {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/vessels", vessel.id, "documents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/vessels"] }); // Refresh vessel list to show status changes
      setShowUploadDialog(false);
      setSelectedFile(null);
      reset();
      toast({
        title: "Success",
        description: "Document uploaded successfully",
      });
    },
    onError: (error) => {
      console.error("Error uploading document:", error);
      toast({
        title: "Error",
        description: "Failed to upload document",
        variant: "destructive",
      });
    },
  });

  // Delete document mutation
  const deleteMutation = useMutation({
    mutationFn: async (documentId: number) => {
      const response = await fetch(`/api/vessels/${vessel.id}/documents/${documentId}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error("Failed to delete document");
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/vessels", vessel.id, "documents"] });
      toast({
        title: "Success",
        description: "Document deleted successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to delete document",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: DocumentUploadData) => {
    uploadMutation.mutate(data);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const getDocumentTypeLabel = (type: string) => {
    return DOCUMENT_TYPES.find(dt => dt.value === type)?.label || type;
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <FileText className="h-5 w-5" />
            <span>Vessel Documents</span>
          </CardTitle>
          <Dialog open={showUploadDialog} onOpenChange={setShowUploadDialog}>
            <DialogTrigger asChild>
              <Button size="sm">
                <Plus className="h-4 w-4 mr-2" />
                Upload Document
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Upload Vessel Document</DialogTitle>
              </DialogHeader>
              <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                <div>
                  <Label htmlFor="documentType">Document Type</Label>
                  <Select onValueChange={(value) => setValue("documentType", value)}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select document type" />
                    </SelectTrigger>
                    <SelectContent>
                      {DOCUMENT_TYPES.map((type) => (
                        <SelectItem key={type.value} value={type.value}>
                          {type.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  {errors.documentType && (
                    <p className="text-sm text-red-500 mt-1">{errors.documentType.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="documentName">Document Name</Label>
                  <Input
                    id="documentName"
                    {...register("documentName")}
                    placeholder="Enter document name"
                    className={errors.documentName ? "border-red-500" : ""}
                  />
                  {errors.documentName && (
                    <p className="text-sm text-red-500 mt-1">{errors.documentName.message}</p>
                  )}
                </div>

                <div>
                  <Label htmlFor="document">Upload File</Label>
                  <Input
                    id="document"
                    type="file"
                    accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
                    onChange={handleFileChange}
                    className="cursor-pointer"
                  />
                  {selectedFile && (
                    <p className="text-sm text-green-600 mt-1">
                      Selected: {selectedFile.name}
                    </p>
                  )}
                </div>

                <div>
                  <Label htmlFor="notes">Notes (Optional)</Label>
                  <Textarea
                    id="notes"
                    {...register("notes")}
                    placeholder="Add any notes about this document"
                    rows={3}
                  />
                </div>

                <div className="flex space-x-2">
                  <Button 
                    type="submit" 
                    disabled={uploadMutation.isPending || !selectedFile}
                    className="flex-1"
                  >
                    {uploadMutation.isPending ? (
                      <>
                        <Upload className="h-4 w-4 mr-2 animate-spin" />
                        Uploading...
                      </>
                    ) : (
                      <>
                        <Upload className="h-4 w-4 mr-2" />
                        Upload
                      </>
                    )}
                  </Button>
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setShowUploadDialog(false)}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </CardHeader>
      <CardContent>
        {isLoading ? (
          <div className="text-center py-4">
            <p className="text-gray-500">Loading documents...</p>
          </div>
        ) : documents.length === 0 ? (
          <div className="text-center py-8">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-500 mb-4">No documents uploaded yet</p>
            <p className="text-sm text-gray-400">
              Upload documents like Bill of Lading, Commercial Invoice, or Customs Release
            </p>
          </div>
        ) : (
          <div className="space-y-3">
            {documents.map((document: any) => (
              <div key={document.id} className="flex items-center justify-between p-3 border rounded-lg">
                <div className="flex items-center space-x-3 flex-1">
                  <FileText className="h-5 w-5 text-gray-500" />
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2">
                      <h4 className="font-medium text-gray-900 truncate">
                        {document.documentName}
                      </h4>
                      <Badge variant="secondary" className="text-xs">
                        {getDocumentTypeLabel(document.documentType)}
                      </Badge>
                      {document.documentType === 'customs_release' && (
                        <Badge className="bg-emerald-100 text-emerald-800 text-xs">
                          Auto-completes vessel
                        </Badge>
                      )}
                    </div>
                    <p className="text-sm text-gray-500 truncate">
                      {document.fileName}
                    </p>
                    {document.notes && (
                      <p className="text-xs text-gray-400 mt-1 truncate">
                        {document.notes}
                      </p>
                    )}
                    <p className="text-xs text-gray-400">
                      Uploaded {new Date(document.uploadedAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
                <div className="flex items-center space-x-1">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => window.open(`/uploads/${document.fileName}`, '_blank')}
                  >
                    <ExternalLink className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => deleteMutation.mutate(document.id)}
                    disabled={deleteMutation.isPending}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/pages/operations.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import MainLayout from "@/components/layout/main-layout";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Package, Ship, TrendingUp, Users, Filter, Calendar, Download } from "lucide-react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { COUNTRIES } from "@/lib/constants";
// Define status colors for vessel status badges
const STATUS_COLORS: Record<string, string> = {
  pending: "bg-yellow-100 text-yellow-800",
  nominated: "bg-blue-100 text-blue-800",
  completed: "bg-emerald-100 text-emerald-800",
  default: "bg-gray-100 text-gray-800"
};



export default function Reports() {
  const { isAuthenticated, isLoading } = useAuth();
  const [selectedSupplier, setSelectedSupplier] = useState<string | null>(null);
  const [selectedCountry, setSelectedCountry] = useState<string | null>(null);
  const [dateRange, setDateRange] = useState({ from: "", to: "" });

  // Fetch data
  const { data: contracts = [] } = useQuery({
    queryKey: ["/api/contracts"],
    enabled: isAuthenticated,
  });

  const { data: vessels = [] } = useQuery({
    queryKey: ["/api/vessels"],
    enabled: isAuthenticated,
  });

  const { data: requests = [] } = useQuery({
    queryKey: ["/api/requests"],
    enabled: isAuthenticated,
  });

  if (isLoading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return null;
  }

  // Helper function to filter data by date range
  const filterByDateRange = (data: any[], dateField: string) => {
    if (!dateRange.from && !dateRange.to) return data;

    return data.filter((item: any) => {
      const itemDate = new Date(item[dateField]);
      const fromDate = dateRange.from ? new Date(dateRange.from) : null;
      const toDate = dateRange.to ? new Date(dateRange.to) : null;

      if (fromDate && toDate) {
        return itemDate >= fromDate && itemDate <= toDate;
      } else if (fromDate) {
        return itemDate >= fromDate;
      } else if (toDate) {
        return itemDate <= toDate;
      }
      return true;
    });
  };

  // Apply date filtering to contracts and vessels
  const filteredContracts = filterByDateRange(contracts as any[], 'createdAt');
  const filteredVesselsForDateRange = filterByDateRange(vessels as any[], 'createdAt');

  // Process suppliers data for chart using filtered contracts
  const suppliersData = filteredContracts.reduce((acc: any[], contract: any) => {
    const supplierName = contract.supplierName || 'Unknown Supplier';
    const existing = acc.find(item => item.supplier === supplierName);

    if (existing) {
      existing.contracts += 1;
      existing.quantity += contract.quantity || 0;
      existing.vessels += filteredVesselsForDateRange.filter((v: any) => v.contractId === contract.id).length;
    } else {
      acc.push({
        supplier: supplierName,
        vessels: filteredVesselsForDateRange.filter((v: any) => v.contractId === contract.id).length,
        quantity: contract.quantity || 0,
        contracts: 1
      });
    }

    return acc;
  }, []);

  // Get supplier-specific data when a supplier is selected
  const selectedSupplierData = selectedSupplier 
    ? suppliersData.find(s => s.supplier === selectedSupplier) 
    : null;

  // Calculate quantity metrics
  const calculateQuantityMetrics = (contracts: any[], vessels: any[]) => {
    const totalContracted = contracts.reduce((sum: number, contract: any) => sum + (contract.quantity || 0), 0);

    // Calculate arrived quantity from discharged vessel quantities
    const arrivedQuantity = vessels.reduce((sum: number, vessel: any) => {
      return sum + (vessel.dischargedQuantity || 0);
    }, 0);

    const remainingQuantity = totalContracted - arrivedQuantity;

    return {
      totalContracted,
      arrivedQuantity,
      remainingQuantity
    };
  };

  // Get metrics for selected supplier or all
  const relevantContracts = selectedSupplier 
    ? filteredContracts.filter((c: any) => c.supplierName === selectedSupplier)
    : filteredContracts;

  const relevantVessels = selectedSupplier
    ? filteredVesselsForDateRange.filter((vessel: any) => {
        const contract = filteredContracts.find((c: any) => c.id === vessel.contractId);
        return contract?.supplierName === selectedSupplier;
      })
    : filteredVesselsForDateRange;

  const quantityMetrics = calculateQuantityMetrics(relevantContracts, relevantVessels);

  // Calculate totals (either for selected supplier or all)
  const displayTotals = {
    totalSuppliers: selectedSupplierData ? 1 : suppliersData.length,
    totalVessels: selectedSupplierData ? selectedSupplierData.vessels : filteredVesselsForDateRange.length,
    totalContracts: selectedSupplierData ? selectedSupplierData.contracts : filteredContracts.length,
    totalContracted: quantityMetrics.totalContracted || 0,
    arrivedQuantity: quantityMetrics.arrivedQuantity || 0,
    remainingQuantity: quantityMetrics.remainingQuantity || 0
  };

  // Filter vessels by selected supplier, country, and date range
  const filteredVessels = filteredVesselsForDateRange.filter((vessel: any) => {
    const contract = filteredContracts.find((c: any) => c.id === vessel.contractId);

    const supplierMatch = selectedSupplier ? contract?.supplierName === selectedSupplier : true;
    const countryMatch = selectedCountry ? vessel.countryOfOrigin === selectedCountry : true;

    return supplierMatch && countryMatch;
  });

  // Get unique suppliers for the dropdown
  const availableSuppliers = Array.from(new Set(filteredContracts.map((c: any) => c.supplierName).filter(Boolean)));

  // Prepare vessels data with contract information
  const vesselsWithContractInfo = filteredVessels.map((vessel: any) => {
    const contract = filteredContracts.find((c: any) => c.id === vessel.contractId);
    return {
      ...vessel,
      supplierName: contract?.supplierName || 'Unknown',
      contractQuantity: contract?.quantity || 0,
    };
  });

  const getStatusBadge = (status: string) => {
    const colorClass = STATUS_COLORS[status] || STATUS_COLORS.default;
    return (
      <Badge variant="outline" className={`${colorClass} border-0`}>
        {status}
      </Badge>
    );
  };

  const formatDate = (dateString: string) => {
    if (!dateString) return 'Not set';
    return new Date(dateString).toLocaleDateString();
  };

  return (
    <MainLayout title="Reports" subtitle="Comprehensive reports and analytics with date filtering">
      <div className="p-6 space-y-6">
        {/* Filters Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Filter className="h-5 w-5" />
              Filters & Controls
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 lg:grid-cols-4 gap-4 items-end">
              <div className="space-y-2">
                <Label htmlFor="from-date">From Date</Label>
                <Input
                  id="from-date"
                  type="date"
                  value={dateRange.from}
                  onChange={(e) => setDateRange(prev => ({ ...prev, from: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="to-date">To Date</Label>
                <Input
                  id="to-date"
                  type="date"
                  value={dateRange.to}
                  onChange={(e) => setDateRange(prev => ({ ...prev, to: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <Label>Filter by Supplier</Label>
                <Select value={selectedSupplier || "all"} onValueChange={(value) => setSelectedSupplier(value === "all" ? null : value)}>
                  <SelectTrigger>
                    <SelectValue placeholder="All Suppliers" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Suppliers</SelectItem>
                    {availableSuppliers.map((supplier) => (
                      <SelectItem key={supplier} value={supplier}>{supplier}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  onClick={() => {
                    setDateRange({ from: "", to: "" });
                    setSelectedSupplier(null);
                    setSelectedCountry(null);
                  }}
                  disabled={!dateRange.from && !dateRange.to && !selectedSupplier && !selectedCountry}
                >
                  Clear All
                </Button>
                <Button variant="outline" className="flex items-center gap-2">
                  <Download className="h-4 w-4" />
                  Export
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

          {/* KPI Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <Card className={`bg-white border ${selectedSupplier ? 'border-blue-200 bg-blue-50/30' : 'border-secondary-200'}`}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Total Contracted</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {displayTotals.totalContracted.toLocaleString()} tons
                    </p>
                    {selectedSupplier && (
                      <p className="text-xs text-blue-600 mt-1 font-medium">
                        for {selectedSupplier}
                      </p>
                    )}
                  </div>
                  <div className={`w-12 h-12 ${selectedSupplier ? 'bg-blue-200' : 'bg-blue-100'} rounded-lg flex items-center justify-center`}>
                    <Package className={`${selectedSupplier ? 'text-blue-700' : 'text-blue-600'}`} size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className={`bg-white border ${selectedSupplier ? 'border-green-200 bg-green-50/30' : 'border-secondary-200'}`}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Quantity Discharged</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {displayTotals.arrivedQuantity.toLocaleString()} tons
                    </p>
                    <p className="text-xs text-green-600 mt-1">
                      {displayTotals.totalContracted > 0 ? 
                        `${((displayTotals.arrivedQuantity / displayTotals.totalContracted) * 100).toFixed(1)}% discharged` : 
                        'No contracts yet'
                      }
                    </p>
                  </div>
                  <div className={`w-12 h-12 ${selectedSupplier ? 'bg-green-200' : 'bg-green-100'} rounded-lg flex items-center justify-center`}>
                    <Ship className={`${selectedSupplier ? 'text-green-700' : 'text-green-600'}`} size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className={`bg-white border ${selectedSupplier ? 'border-orange-200 bg-orange-50/30' : 'border-secondary-200'}`}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">Remaining to Arrive</p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {displayTotals.remainingQuantity.toLocaleString()} tons
                    </p>
                    <p className="text-xs text-orange-600 mt-1">
                      {displayTotals.totalContracted > 0 ? 
                        `${((displayTotals.remainingQuantity / displayTotals.totalContracted) * 100).toFixed(1)}% pending` : 
                        'No pending deliveries'
                      }
                    </p>
                  </div>
                  <div className={`w-12 h-12 ${selectedSupplier ? 'bg-orange-200' : 'bg-orange-100'} rounded-lg flex items-center justify-center`}>
                    <TrendingUp className={`${selectedSupplier ? 'text-orange-700' : 'text-orange-600'}`} size={20} />
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className={`bg-white border ${selectedSupplier ? 'border-purple-200 bg-purple-50/30' : 'border-secondary-200'}`}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-secondary-600">
                      {selectedSupplier ? 'Active Vessels' : 'Total Suppliers'}
                    </p>
                    <p className="text-3xl font-bold text-secondary-900">
                      {selectedSupplier ? displayTotals.totalVessels : displayTotals.totalSuppliers}
                    </p>
                    {selectedSupplier ? (
                      <p className="text-xs text-purple-600 mt-1">
                        vessels in operation
                      </p>
                    ) : (
                      <p className="text-xs text-purple-600 mt-1">
                        active suppliers
                      </p>
                    )}
                  </div>
                  <div className={`w-12 h-12 ${selectedSupplier ? 'bg-purple-200' : 'bg-purple-100'} rounded-lg flex items-center justify-center`}>
                    {selectedSupplier ? (
                      <Ship className={`${selectedSupplier ? 'text-purple-700' : 'text-purple-600'}`} size={20} />
                    ) : (
                      <Users className={`${selectedSupplier ? 'text-purple-700' : 'text-purple-600'}`} size={20} />
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>



          {/* Vessels Table */}
          <Card className="bg-white border border-secondary-200">
            <CardHeader>
              <div className="flex justify-between items-center">
                <div>
                  <CardTitle className="text-lg font-bold text-secondary-900">
                    Vessels Overview
                    {(selectedSupplier || selectedCountry) && (
                      <span className="text-blue-600 font-normal">
                        {selectedSupplier && ` - Supplier: ${selectedSupplier}`}
                        {selectedCountry && ` - Country: ${selectedCountry}`}
                      </span>
                    )}
                  </CardTitle>
                  <p className="text-sm text-secondary-600">
                    {(selectedSupplier || selectedCountry || dateRange.from || dateRange.to) ? 
                      `Showing ${filteredVessels.length} filtered vessels` : 
                      `All ${filteredVesselsForDateRange.length} vessels`}
                    {(dateRange.from || dateRange.to) && (
                      <span className="block text-xs text-blue-600">
                        Date filtered: {dateRange.from && `From ${dateRange.from}`} {dateRange.to && `To ${dateRange.to}`}
                      </span>
                    )}
                  </p>
                </div>
                <div className="flex gap-2">
                  <Select value={selectedCountry || "all"} onValueChange={(value) => setSelectedCountry(value === "all" ? null : value)}>
                    <SelectTrigger className="w-48">
                      <Filter className="w-4 h-4 mr-2" />
                      <SelectValue placeholder="Filter by country" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Countries</SelectItem>
                      {COUNTRIES.map((country) => (
                        <SelectItem key={country} value={country}>{country}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  {(selectedSupplier || selectedCountry) && (
                    <Button 
                      variant="outline" 
                      onClick={() => {
                        setSelectedSupplier(null);
                        setSelectedCountry(null);
                      }}
                    >
                      Clear Filters
                    </Button>
                  )}
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Vessel Name</TableHead>
                      <TableHead>Supplier</TableHead>
                      <TableHead>Cargo Type</TableHead>
                      <TableHead>Country of Origin</TableHead>
                      <TableHead>Quantity</TableHead>
                      <TableHead>Discharge Port</TableHead>
                      <TableHead>ETA</TableHead>
                      <TableHead>Status</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {vesselsWithContractInfo.map((vessel: any) => (
                      <TableRow 
                        key={vessel.id}
                        className={selectedSupplier && vessel.supplierName === selectedSupplier ? 'bg-blue-50' : ''}
                      >
                        <TableCell className="font-medium">{vessel.vesselName}</TableCell>
                        <TableCell>
                          <Badge 
                            variant="outline" 
                            className={`cursor-pointer ${vessel.supplierName === selectedSupplier ? 'bg-blue-100 text-blue-800 border-blue-300' : ''}`}
                            onClick={() => setSelectedSupplier(vessel.supplierName)}
                          >
                            {vessel.supplierName}
                          </Badge>
                        </TableCell>
                        <TableCell>{vessel.cargoType}</TableCell>
                        <TableCell>
                          <Badge 
                            variant="outline" 
                            className={`cursor-pointer ${vessel.countryOfOrigin === selectedCountry ? 'bg-green-100 text-green-800 border-green-300' : ''}`}
                            onClick={() => setSelectedCountry(vessel.countryOfOrigin)}
                          >
                            {vessel.countryOfOrigin || 'Not specified'}
                          </Badge>
                        </TableCell>
                        <TableCell>{vessel.quantity?.toLocaleString()} tons</TableCell>
                        <TableCell>{vessel.portOfDischarge || 'Not specified'}</TableCell>
                        <TableCell>{formatDate(vessel.eta)}</TableCell>
                        <TableCell>{getStatusBadge(vessel.status)}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                {vesselsWithContractInfo.length === 0 && (
                  <div className="text-center py-8">
                    <Ship className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">
                      {selectedSupplier ? `No vessels found for ${selectedSupplier}` : 'No vessels found'}
                    </p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
      </div>
    </MainLayout>
  );
}

