import express, { type Request, Response, NextFunction } from "express";
import { registerLocalRoutes } from "./routes.local";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerLocalRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
    console.log(`\nðŸš€ ImportFlow running locally!`);
    console.log(`ðŸ“ Login with: admin@importflow.local / admin123`);
    console.log(`ðŸŒ Access: http://localhost:${port}\n`);
  });
})();

server/localAuth.ts.content:
import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import MemoryStore from "memorystore";
import { storage } from "./storage";

// Default admin user for local development
const DEFAULT_ADMIN = {
  id: "1",
  email: "admin@importflow.local",
  password: "admin123", // In production, this should be hashed
  firstName: "Admin",
  lastName: "User",
  role: "admin" as const,
  profileImageUrl: ""
};

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const MemoryStoreSession = MemoryStore(session);
  const sessionStore = new MemoryStoreSession({
    checkPeriod: 86400000, // prune expired entries every 24h
  });

  return session({
    secret: process.env.SESSION_SECRET || 'local-development-secret-key',
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      maxAge: sessionTtl,
    },
  });
}

export async function setupLocalAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  // Initialize default admin user
  try {
    await storage.upsertUser(DEFAULT_ADMIN);
    console.log("âœ“ Default admin user initialized (admin@importflow.local / admin123)");
  } catch (error) {
    console.log("Admin user already exists or error creating:", error);
  }

  // Local Strategy for username/password authentication
  passport.use(new LocalStrategy(
    { usernameField: 'email' },
    async (email: string, password: string, done) => {
      try {
        // Check default admin user first
        if (email === DEFAULT_ADMIN.email && password === DEFAULT_ADMIN.password) {
          const user = { ...DEFAULT_ADMIN };
          delete (user as any).password; // Remove password from user object
          return done(null, user);
        }

        // Check database users
        try {
          const dbUser = await storage.getUserByEmail(email);
          if (dbUser && dbUser.password === password) {
            const user = { ...dbUser };
            delete (user as any).password; // Remove password from user object
            return done(null, user);
          }
        } catch (dbError) {
          console.log("Database user check failed:", dbError);
        }

        return done(null, false, { message: 'Invalid credentials' });
      } catch (error) {
        return done(error);
      }
    }
  ));

  passport.serializeUser((user: any, done) => {
    done(null, user.id);
  });

  passport.deserializeUser(async (id: string, done) => {
    try {
      // Check default admin user first
      if (id === DEFAULT_ADMIN.id) {
        const user = { ...DEFAULT_ADMIN };
        delete (user as any).password;
        return done(null, user);
      }

      // Check database users
      try {
        const dbUser = await storage.getUser(id);
        if (dbUser) {
          const user = { ...dbUser };
          delete (user as any).password;
          return done(null, user);
        }
      } catch (dbError) {
        console.log("Database user deserialize failed:", dbError);
      }

      done(null, false);
    } catch (error) {
      done(error);
    }
  });

  // Login route
  app.post('/api/login', (req, res, next) => {
    passport.authenticate('local', (err: any, user: any, info: any) => {
      if (err) {
        return res.status(500).json({ message: 'Authentication error' });
      }
      if (!user) {
        return res.status(401).json({ message: info?.message || 'Invalid credentials' });
      }

      req.logIn(user, (err) => {
        if (err) {
          return res.status(500).json({ message: 'Session error' });
        }
        return res.json({ user, message: 'Login successful' });
      });
    })(req, res, next);
  });

  // Logout route
  app.post('/api/logout', (req, res) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ message: 'Logout error' });
      }
      res.json({ message: 'Logout successful' });
    });
  });

  // Get current user route
  app.get('/api/auth/user', (req, res) => {
    if (req.isAuthenticated() && req.user) {
      res.json(req.user);
    } else {
      res.status(401).json({ message: 'Not authenticated' });
    }
  });

  // Registration route
  app.post('/api/register', async (req, res) => {
    try {
      const { email, password, firstName, lastName, role } = req.body;

      if (!email || !password || !firstName || !lastName) {
        return res.status(400).json({ message: 'Missing required fields' });
      }

      // Check if user already exists
      try {
        const existingUser = await storage.getUserByEmail(email);
        if (existingUser) {
          return res.status(400).json({ message: 'User already exists' });
        }
      } catch (error) {
        // User doesn't exist, continue with registration
      }

      // Create new user
      const newUser = {
        id: Date.now().toString(), // Simple ID generation for demo
        email,
        password, // In production, hash this password
        firstName,
        lastName,
        role: role || 'admin', // Default role
        profileImageUrl: ''
      };

      await storage.upsertUser(newUser);

      // Remove password from response
      const userResponse = { ...newUser };
      delete (userResponse as any).password;

      res.status(201).json({ 
        user: userResponse, 
        message: 'User registered successfully' 
      });
    } catch (error) {
      console.error('Registration error:', error);
      res.status(500).json({ message: 'Registration failed' });
    }
  });
}

export const isAuthenticated: RequestHandler = (req, res, next) => {
  if (req.isAuthenticated()) {
    return next();
  }
  res.status(401).json({ message: 'Not authenticated' });
};

server/routes.local.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupLocalAuth, isAuthenticated } from "./localAuth";
import {
  insertNeedSchema,
  insertRequestSchema,
  insertContractSchema,
  insertLetterOfCreditSchema,
  insertVesselSchema,
  insertDocumentVoteSchema,
  insertRequestVoteSchema,
  insertContractVoteSchema,
  insertVesselLetterOfCreditSchema,
  insertVesselLoadingPortSchema,
} from "@shared/schema";
import { z } from "zod";
import multer from "multer";
import path from "path";
import fs from "fs";

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), "uploads");
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const storage_multer = multer.diskStorage({
  destination: function (req, file, cb) {
    cb(null, uploadDir);
  },
  filename: function (req, file, cb) {
    const uniqueSuffix = Date.now() + '-' + Math.round(Math.random() * 1E9);
    cb(null, file.fieldname + '-' + uniqueSuffix + path.extname(file.originalname));
  }
});

const upload = multer({ 
  storage: storage_multer,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: function (req, file, cb) {
    const allowedTypes = /pdf|doc|docx/;
    const extname = allowedTypes.test(path.extname(file.originalname).toLowerCase());
    const mimetype = allowedTypes.test(file.mimetype);

    if (mimetype && extname) {
      return cb(null, true);
    } else {
      cb(new Error('Only PDF, DOC, and DOCX files are allowed'));
    }
  }
});

export async function registerLocalRoutes(app: Express): Promise<Server> {
  // Local Auth middleware
  await setupLocalAuth(app);

  // Auth routes are handled in localAuth.ts

  // Dashboard stats
  app.get("/api/dashboard/stats", isAuthenticated, async (req, res) => {
    try {
      const requests = await storage.getRequests();
      const contracts = await storage.getContracts();
      const vessels = await storage.getVessels();
      const lcs = await storage.getLettersOfCredit();

      const stats = {
        totalRequests: requests.length,
        totalContracts: contracts.length,
        totalVessels: vessels.length,
        totalLCs: lcs.length,
